INSERT INTO `tbl_product` VALUES (82,24,20,NULL,'Khu đô thị Phương Đông - Vân Đồn','Xã Đông Xá, Huyện Vân Đồn, Tỉnh Quảng Ninh',NULL,NULL,NULL,NULL,298,'/public/upload/files/ph%C6%B0%C6%A1ng%20%C4%91%C3%B4ng/d3f99c1c4525b87be134.jpg','Liên hệ',NULL,0,'1','<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Chủ đầu tư:&nbsp;: C&ocirc;ng ty Cổ phần Đầu tư X&acirc;y dựng Đ&ocirc; thị Phương Đ&ocirc;ng</p>\r\n<p>\r\n	Diện t&iacute;ch đất nghi&ecirc;n cứu: 171,42 ha (m2)</p>\r\n<p>\r\n	Diện t&iacute;ch c&ocirc;ng tr&igrave;nh c&ocirc;ng cộng, trường học: 2,85 ha<br />\r\n	Diện t&iacute;ch c&ocirc;ng vi&ecirc;n c&acirc;y xanh, khu thể dục thể thao: 9,69 ha<br />\r\n	Loại h&igrave;nh sản phẩm: biệt thự, liền kề, shophouse, Chung cư cao cấp, kh&aacute;ch sạn thấp tầng, kh&aacute;ch sạn cao tầng</p>\r\n<p>\r\n	Quy m&ocirc; dự &aacute;n: 19 block liền kề diện t&iacute;ch từ 87.5m2 - 140,5 m2</p>\r\n<p>\r\n	- 26 Block đất dịch vụ kh&aacute;ch sạn cao tầng&nbsp;</p>\r\n<p>\r\n	- 6 s&acirc;n b&oacute;ng đ&aacute; ti&ecirc;u chuẩn quốc tế.&nbsp;</p>\r\n<p>\r\n	- 2 trường mầm non&nbsp;</p>\r\n<p>\r\n	- Quảng trường trung t&acirc;m, bể bơi v&ocirc; cực, b&atilde;i tắm biển d&agrave;i 1km, đường bao biển dạo bộ, ngọn hải đăng, qu&aacute;n c&agrave; ph&ecirc;, nh&agrave; h&agrave;ng hải sản, đ&agrave;i phun nước...</p>','<p>\r\n	&nbsp;</p>\r\n<p>\r\n	T&ecirc;n dự &aacute;n: Khu đ&ocirc; thị nghỉ dưỡng, giải tr&iacute; phức hợp Phương Đ&ocirc;ng V&acirc;n Đồn</p>\r\n<p>\r\n	Địa chỉ: Khu kinh tế V&acirc;n Đồn, tỉnh Quảng Ninh</p>\r\n<p>\r\n	Đơn vị ph&aacute;t triển dự &aacute;n: Khu đ&ocirc; thị Phương Đ&ocirc;ng - V&acirc;n Đồn</p>\r\n<p>\r\n	Tổng diện t&iacute;ch quy hoạch: 171,42 ha</p>\r\n<p>\r\n	Diện t&iacute;ch c&ocirc;ng tr&igrave;nh c&ocirc;ng cộng, trường học: 2,85 ha</p>\r\n<p>\r\n	Diện t&iacute;ch c&ocirc;ng vi&ecirc;n c&acirc;y xanh, khu thể dục thể thao: 9,69 ha</p>\r\n<p>\r\n	Quy hoạch: biệt thự, biệt thự nghỉ dưỡng, shophouse, nh&agrave; ở liền kề, nh&agrave; phố thương mại, Chung cư cao cấp, kh&aacute;ch sạn thấp tầng, kh&aacute;ch sạn cao tầng.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<iframe allow=\"accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/og3CNMGLp0Y\" width=\"560\"></iframe></p>','<p>\r\n	Dự &aacute;n <a href=\"http://bdsquangninh.com/du-an-bds/khu-do-thi-phuong-dong-van-don/82.html\">Khu đ&ocirc; thị Phương Đ&ocirc;ng - V&acirc;n Đồn</a> sở hữu vị tr&iacute; đắc địa nằm ngay cửa ng&otilde; v&agrave;o của khu kinh tế đặc biệt V&acirc;n Đồn. Với vị tr&iacute; phong thủy lưng tựa n&uacute;i, mặt hướng biển v&agrave; được chủ đầu tư d&agrave;nh rất nhiều t&acirc;m huyết bằng những cơ sở hạ tầng ho&agrave;n thiện đồng bộ c&oacute; 1 kh&ocirc;ng 2. Dự &aacute;n được chủ đầu tư định hướng vừa l&agrave; khu đ&ocirc; thị đ&aacute;ng sống bậc nhất vừa l&agrave; khu vực kinh doanh nghỉ dưỡng phức hợp n&ecirc;n hứa hẹn sẽ l&agrave; điểm đến tuyệt vời cho mọi nh&agrave; đầu tư.&nbsp;</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/ph%C6%B0%C6%A1ng%20%C4%91%C3%B4ng/v%E1%BB%8B%20tr%C3%AD%20d%E1%BB%B1%20%C3%A1n.png\" style=\"width: 1080px; height: 610px;\" /></p>\r\n<p>\r\n	<iframe allow=\"accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/dui6DWsXV4U\" width=\"560\"></iframe></p>','<p>\r\n	Từ dự &aacute;n khu đ&ocirc; thị Phương Đ&ocirc;ng - V&acirc;n Đồn c&oacute; thể dễ d&agrave;ng kết nối với c&aacute;c tiện &iacute;ch kh&aacute;c xung quanh.&nbsp;</p>\r\n<ul>\r\n	<li>\r\n		Đền Cửa &Ocirc;ng (Cẩm Phả): 2km</li>\r\n	<li>\r\n		Trung t&acirc;m thị trấn C&aacute;i Rồng: 4km</li>\r\n	<li>\r\n		Th&agrave;nh phố Cẩm Phả: 7km</li>\r\n	<li>\r\n		S&acirc;n bay quốc tế V&acirc;n Đồn: 7km</li>\r\n	<li>\r\n		Cao tốc H&agrave; Nội - Hạ Long - V&acirc;n Đồn &ndash; M&oacute;ng C&aacute;i: 4km</li>\r\n	<li>\r\n		Trung t&acirc;m y tế V&acirc;n Đồn: 3km</li>\r\n	<li>\r\n		Cầu V&acirc;n Đồn 3: liền kề</li>\r\n	<li>\r\n		Casino V&acirc;n Đồn: 6km</li>\r\n	<li>\r\n		Cảng h&agrave;nh kh&aacute;ch Ao Ti&ecirc;n: 7km</li>\r\n	<li>\r\n		Cảng C&aacute;i Rồng: 2,5km</li>\r\n</ul>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/ph%C6%B0%C6%A1ng%20%C4%91%C3%B4ng/k%E1%BA%BFt%20n%E1%BB%91i%20v%C3%B9ng.png\" style=\"width: 772px; height: 821px;\" /></p>','<p>\r\n	<a href=\"http://bdsquangninh.com/du-an-bds/khu-do-thi-phuong-dong-van-don/82.html\">KHu đ&ocirc; thị mới Phương Đ&ocirc;ng - V&acirc;n Đồn</a> được v&iacute; như vi&ecirc;n kim cương nổi l&ecirc;n ngay ngưỡng cửa v&agrave;o Khu kinh tế đặc biệt V&acirc;n Đồn với rất nhiều tiện &iacute;ch nội khu được chủ đầu tư ưu &aacute;i d&agrave;nh tặng cho cư d&acirc;n sinh sống nơi đ&acirc;y.</p>\r\n<p>\r\n	Sở hữu trong m&igrave;nh với 19 Block liền kề, biệt thự để ở, kh&aacute;ch sạn cao cấp, v&agrave; khu nghỉ dưỡng mặt biển. Tổ hợp khu đ&ocirc; thị v&agrave; nghỉ dưỡng Phương Đ&ocirc;ng - V&acirc;n Đồn chắc chắn sẽ mang lại những gi&aacute; trị nghỉ dưỡng v&agrave; tiềm năng tăng gi&aacute; bất động sản v&ocirc; c&ugrave;ng lớn tại Quảng Ninh.</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/MB%20Ph%C6%B0%C6%A1ng%20%C4%90%C3%94ng%200904476233.jpg\" style=\"width: 1180px; height: 642px;\" /></p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/ph%C6%B0%C6%A1ng%20%C4%91%C3%B4ng/Quy%20ho%E1%BA%A1ch%20m%E1%BB%9Bi%20V%C3%A2n%20%C4%90%E1%BB%93n-%20c%C6%A1%20h%E1%BB%99i%20%C4%91%E1%BA%A7u%20t%C6%B0%20l%C3%A0%20%C4%91%C3%A2y-%20-%20VTV24.mp4\" /></p>\r\n<p>\r\n	&nbsp;</p>','<p>\r\n	Dự &aacute;n Phương Đ&ocirc;ng - V&acirc;n Đồn được chủ đầu tư ưu &aacute;i d&agrave;nh tặng cho kh&aacute;ch h&agrave;ng rất nhiều ch&iacute;nh s&aacute;ch b&aacute;n h&agrave;ng đặc biệt:&nbsp;</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/ph%C6%B0%C6%A1ng%20%C4%91%C3%B4ng/z1975362555717_f8ec0981839959c7efbbc917be64c446.jpg\" style=\"width: 1080px; height: 638px;\" /><img alt=\"\" src=\"/public/upload/files/ph%C6%B0%C6%A1ng%20%C4%91%C3%B4ng/z1975362555715_bd6f3fbc7659f1477c34b601ddf641be.jpg\" style=\"width: 1080px; height: 619px;\" /><img alt=\"\" src=\"/public/upload/files/ph%C6%B0%C6%A1ng%20%C4%91%C3%B4ng/z1975362555719_ba0f41fb1289514b79e46ee50d1fef60.jpg\" style=\"width: 1080px; height: 617px;\" /><img alt=\"\" src=\"/public/upload/files/ph%C6%B0%C6%A1ng%20%C4%91%C3%B4ng/z1975362552198_a0beea1e567572d70a8b11781b0cc0b7.jpg\" style=\"width: 1080px; height: 610px;\" /></p>',NULL,NULL,'Tổng quan khu đô thị Phương Đông - Vân Đồn','Vị Trí dự án Tựa Sơn - Hướng Hải','Liên kết vùng xung quanh dự án','Tiện ích nội khu dự án','Chính sách bán hàng dự án',NULL,NULL,NULL,'Đất nền liền kề, Biệt thự nghỉ dưỡng, bãi tắm Phương Đông, Vân Đồn','25-6-2020','15-7-2020','khu-do-thi-phuong-dong-van-don','Dự án khu đô thị mới, Đất nền liền kề, Biệt thự nghỉ dưỡng, bãi tắm, Phương Đông, Vân Đồn',61,'Dự án Phương Đông, Vân Đồn','0'),(81,24,22,NULL,'Chung cư The Dragon Castle Hạ Long','Đường Cái Lân, Phường Bãi Cháy',NULL,NULL,NULL,NULL,1585,'/public/upload/files/the%20dragon%20castle/c30e44c0c6f63ea867e7.jpg',NULL,NULL,1,'1','<ol>\r\n	<li>\r\n		&diams; T&ecirc;n dự &aacute;n: The Dragon Castle Hạ Long</li>\r\n	<li>\r\n		&diams; Vị tr&iacute;: Thuộc Khu đ&ocirc; thị Kings Town, Khu 10, B&atilde;i Ch&aacute;y, Hạ Long</li>\r\n	<li>\r\n		&diams; Chủ đầu tư: C&ocirc;ng Ty CP Tổ Chức Nh&agrave; Quốc Gia (N.H.O)</li>\r\n	<li>\r\n		&diams; Diện t&iacute;ch: 17.794 m2</li>\r\n	<li>\r\n		&diams; Số tầng: 24 tầng</li>\r\n	<li>\r\n		&diams; Số t&ograve;a: 3 t&ograve;a</li>\r\n	<li>\r\n		&diams; Số căn hộ: 1.275 căn hộ chung cư&nbsp;</li>\r\n	<li>\r\n		&diams; Loại h&igrave;nh căn hộ: Căn Hộ 1 Ph&ograve;ng ngủ, 2 ph&ograve;ng ngủ v&agrave; Căn hộ 3 ph&ograve;ng ngủ</li>\r\n	<li>\r\n		&diams; Diện t&iacute;ch: 53,25m2 - 56,64m2 - 65,08m2 - 90,98m2</li>\r\n	<li>\r\n		&diams; Tiện &iacute;ch nội khu: Trung t&acirc;m thương mại, Bể bơi 4 m&ugrave;a, Bể bơi v&ocirc; cực, c&ocirc;ng vi&ecirc;n, S&acirc;n chơi trẻ em, Trường học, Khu thể thao, Vườn BBQ, ph&ograve;ng tập Gym...</li>\r\n	<li>\r\n		&diams; H&igrave;nh thức sở hữu: L&acirc;u d&agrave;i</li>\r\n</ol>','<p>\r\n	<a href=\"http://bdsquangninh.com/du-an-bds/chung-cu-the-dragon-castle-ha-long/81.html\">The Dragon Castle Hạ Long</a> l&agrave; dự &aacute;n căn hộ chung cư thuộc Khu độ thị King&#39;s Town, phường B&atilde;i Ch&aacute;y. Chủ đầu tư dự &aacute;n l&agrave; C&ocirc;ng Ty CP Tổ Chức Nh&agrave; Quốc Gia (N.H.O). Dự &aacute;n tọa lạc tại khu vực s&ocirc;i động bậc nhất Khu 10 phường B&atilde;i Ch&aacute;y, Hạ Long nhưng lại c&oacute; mức gi&aacute; b&igrave;nh d&acirc;n ph&ugrave; hợp với nhiều gia đ&igrave;nh.&nbsp;</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/the%20dragon%20castle/dragon-castle-ha-long-phoi-canh.jpg\" style=\"width: 800px; height: 800px;\" /></p>','<p>\r\n	Dự &aacute;n tọa lạc trong khu đ&ocirc; thị King&#39;s Town nằm ngay b&ecirc;n cạnh cầu B&atilde;i Ch&aacute;y hướng view ra Vịnh cửa Lục.&nbsp;</p>\r\n<p>\r\n	<img alt=\"Vị Trí dự án The Dragon Castle Hạ Long\" src=\"/public/upload/files/the%20dragon%20castle/vi-tri-the-dragon-castle-ha-long.jpg\" style=\"width: 1080px; height: 632px;\" /></p>\r\n<p>\r\n	&nbsp;</p>','<p>\r\n	Từ <a href=\"http://bdsquangninh.com/du-an-bds/chung-cu-the-dragon-castle-ha-long/81.html\">dự &aacute;n The Dragon Castle Hạ Long</a> bạn chỉ mất 5 ph&uacute;t để kết nối với rất nhiều c&aacute;c tiện &iacute;ch C&ocirc;ng vi&ecirc;n Hạ Long Park, C&aacute;p Treo Nữ Ho&agrave;ng, bệnh viện B&atilde;i Ch&aacute;y, bến xe B&atilde;i Ch&aacute;y, Chợ Vườn Đ&agrave;o...</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/the%20dragon%20castle/dragon-castle-ha-long-tien-ich.jpg\" style=\"width: 500px; height: 499px;\" /></p>\r\n<p>\r\n	Dự &aacute;n được thiết kế bởi c&aacute;c kiến tr&uacute;c sư h&agrave;ng đầu H&agrave;n Quốc bằng những ng&ocirc;n ngữ phong c&aacute;ch thiết kế th&ocirc;ng minh, năng động đậm chất H&agrave;n Quốc với Trung t&acirc;m thương mại, Bể bơi 4 m&ugrave;a, Bể bơi v&ocirc; cực, c&ocirc;ng vi&ecirc;n, S&acirc;n chơi trẻ em, Trường học, Khu thể thao, Vườn BBQ, ph&ograve;ng tập Gym... Dự &aacute;n Chắc chắn dự &aacute;n sẽ l&agrave; nơi &aacute;n cư tuyệt vời cho c&aacute;c gia đ&igrave;nh.</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/the%20dragon%20castle/tien%20ich%20du%20an%20the%20dragon%20castle.jpg\" style=\"width: 1080px; height: 554px;\" /></p>\r\n<p>\r\n	Dự &aacute;n The Dragon Castle được thiết kế x&acirc;y dựng th&agrave;nh m&ocirc; h&igrave;nh cộng đồng cư d&acirc;n kh&eacute;p k&iacute;n với đầy đủ c&aacute;c dịch vụ tiện &iacute;ch b&ecirc;n trong c&ugrave;ng với rất nhiều ti&ecirc;u chuẩn an ninh tối ưu ph&iacute;a trong gi&uacute;p cư d&acirc;n y&ecirc;n t&acirc;m sinh sống trong dự &aacute;n.&nbsp;</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/the%20dragon%20castle/c30e44c0c6f63ea867e7.jpg\" style=\"width: 1456px; height: 744px;\" /></p>','<p>\r\n	Dự &aacute;n The Dragon Castle Hạ Long được chủ đầu tư đặt rất nhiều t&acirc;m huyết với thiết kế v&ocirc; c&ugrave;ng th&ocirc;ng minh với rất nhiều lựa chọn cho kh&aacute;ch h&agrave;ng về diện t&iacute;ch căn hộ từ 43,28 m2 đến 90,95m2 cho căn hộ từ 1 ph&ograve;ng ngủ, 2 ph&ograve;ng ngủ v&agrave; 3 ph&ograve;ng ngủ; trong đo c&aacute;c loại h&igrave;nh căn hộ 1 v&agrave; 2 ph&ograve;ng ngủ c&oacute; rất nhiều lựa chọn diện t&iacute;ch kh&aacute;c nhau cho từng đối tượng gia đ&igrave;nh cụ thể.&nbsp;</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/the%20dragon%20castle/the-dragon-castle-mat-bang.jpg\" /></p>\r\n<p>\r\n	&nbsp;</p>','<p>\r\n	Căn hộ dự &aacute;n The Dragon Castle Hạ Long được thiết kế h&agrave;i h&ograve;a với thi&ecirc;n nhi&ecirc;n, đ&oacute;n nhận nhiều mảng &aacute;nh s&aacute;ng tự nhi&ecirc;n ph&ugrave; hợp với phong c&aacute;ch sống của người phương đ&ocirc;ng n&oacute;i chung v&agrave; người Việt Nam n&oacute;i ri&ecirc;ng.</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/the%20dragon%20castle/dragon-castle-ha-long-thiet-ke-can-ho-1.jpg\" style=\"width: 1600px; height: 1131px;\" /></p>','<h1>\r\n	<img alt=\"\" src=\"/public/upload/files/the%20dragon%20castle/4444.png\" style=\"width: 1512px; height: 830px;\" /></h1>\r\n<h1>\r\n	<span style=\"color:#ff0000;\"><strong>Li&ecirc;n hệ ngay với ch&uacute;ng t&ocirc;i để nhận th&ocirc;ng tin ch&iacute;nh s&aacute;ch b&aacute;n h&agrave;ng dự &aacute;n.</strong></span></h1>',NULL,'Tổng quan dự án The Dragon Castle Hạ Long','Vị Trí dự án The Dragon Castle Hạ Long','Tiện ích xung quanh dự án The Dragon Castle Hạ Long','Mặt bằng chi tiết dự án','Thiết kế nội thất căn hộ','Chính sách bán hàng dự án',NULL,NULL,'Chung cư The Dragon Castle Hạ Long, Căn Hộ phù hợp với vợ chồng trẻ','30-1-2020','14-10-2021','chung-cu-the-dragon-castle-ha-long','The Dragon Castle Hạ Long dự án Căn hộ chung cư giá rẻ rất phù hợp với các cặp vợ chồng trẻ mới cưới khả năng tài chính còn hạn hẹp.',18,'The Dragon Castle','0'),(83,24,22,NULL,'FLC GRAND VILLA HALONG','phường Hồng Hà, thành phố Hạ Long, tỉnh Quảng Ninh',NULL,NULL,NULL,NULL,87,'/public/upload/files/FLC/anh-tong-the-16-1024x683.png',NULL,NULL,0,'1','<p>\r\n	T&ecirc;n dự &aacute;n: FLC HALONG BAY GOLF CLUB &amp; LUXURY RESORT<br />\r\n	Vị tr&iacute;: Cột 3 &ndash; Cột 8, phường Hồng H&agrave;, th&agrave;nh phố Hạ Long, tỉnh Quảng Ninh<br />\r\n	Quy m&ocirc; dự &aacute;n: 157ha<br />\r\n	Tổng mức đầu tư: 3.400 tỷ<br />\r\n	Sản phẩm: Kh&aacute;ch sạn nghỉ dưỡng 5 sao FLC Grand Hotel, trung t&acirc;m hội nghị quốc tế, biệt thự cao cấp<br />\r\n	&bull; 574 căn hộ kh&aacute;ch sạn ti&ecirc;u chuẩn 5 sao (1-4 ph&ograve;ng ngủ)<br />\r\n	&bull; 364 căn biệt thự cao cấp với 100% c&oacute; bể bơi ri&ecirc;ng<br />\r\n	Quy hoạch tổng thể &amp; s&acirc;n golf: Schmidt &ndash; Curley (Mỹ)<br />\r\n	Thiết kế cảnh quan: West Green (Canada)<br />\r\n	Thiết kế kiến tr&uacute;c kh&aacute;ch sạn: S Design (T&acirc;y Ban Nha)<br />\r\n	Thiết kế kiến tr&uacute;c biệt thự: R Design (Singapore)</p>','<p>\r\n	FLC Grand Villa Halong &ndash; Nơi bức họa ph&agrave;m nằm giữa k&igrave; quan thi&ecirc;n nhi&ecirc;n thế giới tạo n&ecirc;n đẳng cấp thượng lưu của gia chủ<br />\r\n	Tr&ecirc;n địa thế tọa sơn vọng thủy, từ ngọn đồi dốc thoải, &ocirc;m trọn v&agrave;o l&ograve;ng tầm nh&igrave;n ngoạn mục ra vịnh Hạ Long h&ugrave;ng vỹ. FLC Grand Villa Hạ Long thuộc quẩn thể du lịch v&agrave; nghỉ dưỡng FLC Halong Bay Golf Club &amp; Luxyry Resort.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	FLC Grand Villa Hạ Long tọa lạc tại vị tr&iacute; đắc địa nhất th&agrave;nh phố Hạ Long. Nơi hội tụ những lợi thế s&aacute;ng gi&aacute; nhất khu vực:</p>\r\n<p>\r\n	V&ograve;ng quay mặt trời (b&aacute;n k&iacute;nh 1km)<br />\r\n	Bảo t&agrave;ng &ndash; Quảng Trường Quảng Ninh (b&aacute;n k&iacute;nh 500m)<br />\r\n	B&atilde;i biển Cột 5 &ndash; Cột 8 (800m)<br />\r\n	Bệnh viện Vinmec (2km)<br />\r\n	Bệnh viện tỉnh Quảng Ninh (1,5km)<br />\r\n	Nằm s&aacute;t QL18<br />\r\n	View trọn vịnh Hạ Long<br />\r\n	Trung t&acirc;m th&agrave;nh phố sầm uất</p>','<p>\r\n	FLC Grand Villa Hạ Long tọa lạc tại vị tr&iacute; đắc địa nhất th&agrave;nh phố Hạ Long. Nơi hội tụ những lợi thế s&aacute;ng gi&aacute; nhất khu vực:</p>\r\n<p>\r\n	V&ograve;ng quay mặt trời (b&aacute;n k&iacute;nh 1km)<br />\r\n	Bảo t&agrave;ng &ndash; Quảng Trường Quảng Ninh (b&aacute;n k&iacute;nh 500m)<br />\r\n	B&atilde;i biển Cột 5 &ndash; Cột 8 (800m)<br />\r\n	Bệnh viện Vinmec (2km)<br />\r\n	Bệnh viện tỉnh Quảng Ninh (1,5km)<br />\r\n	Nằm s&aacute;t QL18<br />\r\n	View trọn vịnh Hạ Long<br />\r\n	Trung t&acirc;m th&agrave;nh phố sầm uất</p>','<p>\r\n	Tiện &iacute;ch FLC Grand Villa Hạ Long<br />\r\n	Hệ thống s&acirc;n vườn bậc thang xanh m&aacute;t độc đ&aacute;o của FLC Grand Villa Hạ Long được lấy cảm hứng từ kiến tr&uacute;c biệt thự ho&agrave;ng gia, như đem trọn thi&ecirc;n nhi&ecirc;n v&agrave;o từng ng&ocirc;i nh&agrave;. B&ecirc;n cạnh đ&oacute;, hệ thống đ&agrave;i phun nước hiện đại đem đến kh&ocirc;ng gian thư gi&atilde;n, nơi cư d&acirc;n c&oacute; thể thảnh thơi dạo bước v&agrave; ngắm nh&igrave;n vịnh Hạ Long từ tr&ecirc;n cao.</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/FLC/1619235515556-0-0-852-1363-crop-1619235519365-63754867004729.jpg\" /></p>\r\n<p>\r\n	S&acirc;n golf 18 lỗ đẳng cấp<br />\r\n	Bể bơi trong nh&agrave; &amp; ngo&agrave;i trời<br />\r\n	Trung t&acirc;m hội nghị quốc gia gần 2000 chỗ ngồi<br />\r\n	Ch&ugrave;a thi&ecirc;n ph&uacute;c<br />\r\n	kh&aacute;ch sạn 5 sao<br />\r\n	Nh&agrave; h&agrave;ng &Aacute;, &Acirc;u</p>','<p>\r\n	Thiết kế &amp; Mặt bằng FLC Grand Villa Hạ Long<br />\r\n	Được thiết kế bởi Schmidt &ndash; Curley Design Company, kiệt t&aacute;c s&acirc;n golf của FLC Hạ Long đ&atilde; mang lại cho mỗi căn biệt thự tầm nh&igrave;n độc đ&aacute;o c&oacute; một kh&ocirc;ng hai: với vịnh Hạ Long kỳ vĩ ngay trước mắt, v&agrave; những thảm cỏ xanh m&ecirc;nh mang phủ xanh c&aacute;c triền đồi ph&iacute;a sau, khung cảnh n&agrave;y gi&uacute;p FLC Grand Villa Hạ Long trở th&agrave;nh kh&ocirc;ng gian sống sang trọng b&ecirc;n kỳ quan thế giới, l&agrave; điểm đầu tư xứng tầm của giới thượng lưu khi đến với th&agrave;nh phố Hạ Long.</p>\r\n<p>\r\n	Biệt thự FLC Hạ Long nằm trong quần thể dự &aacute;n nghỉ dưỡng FLC Hạ Long gồm : t&ograve;a KS 21 tầng , trung t&acirc;m hội nghị , s&acirc;n Golf v&agrave; c&aacute;c l&ocirc; biệt thự.</p>\r\n<p>\r\n	Mặt bằng FLC Grand Villa Hạ Long được chia th&agrave;nh 12 l&ocirc; biệt thự đ&aacute;nh số l&ocirc; từ BT1 tới BT12. Dưới đ&acirc;y l&agrave; mặt bằng chi tiết từng l&ocirc;.</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/FLC/image-001.jpg\" style=\"width: 650px; height: 459px;\" /></p>',NULL,NULL,NULL,'Tổng Quan','Vị Trí','Tiện Ích','Mặt Bằng',NULL,NULL,NULL,NULL,'FLC GRAND VILLA HALONG – ĐỘC TÔN MỘT VỊ THẾ – THU TRỌN CẢ KÌ QUAN','14-10-2021','22-10-2021','flc-grand-villa-halong','Biệt thự hội tụ đầy đủ những tiêu chuẩn vàng của dòng bất động sản nghỉ dưỡng cao cấp.',64,'FLC Hạ Long','0'),(72,33,20,NULL,'Uông Bí New City','P. Yên Thanh',NULL,NULL,NULL,NULL,2761,'/public/upload/images/uong-bi-new-city/phoi-canh-du-an-tan-thanh-uong-bi.jpg','11 triệu/m2',NULL,1,'1','<ul>\r\n	<li>\r\n		<strong>Vị tr&iacute;:</strong> P. Y&ecirc;n Thanh, TP. U&ocirc;ng B&iacute;, tỉnh Quảng Ninh</li>\r\n	<li>\r\n		<strong>Chủ đầu tư</strong>: C&ocirc;ng ty Cổ phần Đầu tư X&acirc;y dựng v&agrave; Thương mại T&acirc;n Th&agrave;nh</li>\r\n	<li>\r\n		<strong>Diện t&iacute;ch đất nghi&ecirc;n cứu</strong>: 275.600 (m2)</li>\r\n	<li>\r\n		<strong>Diện t&iacute;ch đất x&acirc;y dựng</strong>: 916.840 (m2)</li>\r\n	<li>\r\n		<strong>Mật độ x&acirc;y dựng</strong>: 33,26%</li>\r\n	<li>\r\n		<strong>Loại h&igrave;nh ph&aacute;t triển</strong>: khu đ&ocirc; thị kiểu mẫu&nbsp;</li>\r\n	<li>\r\n		<strong>Quy m&ocirc; dự &aacute;n</strong>: 716 l&ocirc; Liền kề, Biệt thự, Shophouse diện t&iacute;ch từ 82 m2 &ndash; 366m2</li>\r\n	<li>\r\n		<strong>Ph&acirc;n khu chức năng</strong>: Trung t&acirc;m thương mại, Trường học cấp 1, cấp 2, C&ocirc;ng vi&ecirc;n c&acirc;y xanh.</li>\r\n	<li>\r\n		<strong>Tư vấn thiết kế</strong>: C&ocirc;ng ty Cổ phần tư vấn Licogi</li>\r\n	<li>\r\n		<strong>Quản l&yacute; dự &aacute;n &amp; tư vấn gi&aacute;m s&aacute;t</strong>: tập đo&agrave;n Đất Xanh&nbsp;</li>\r\n</ul>','<p>\r\n	<a href=\"http://bdsquangninh.com/dat-nen/uong-bi-new-city/72.html\">U&ocirc;ng B&iacute;</a> l&agrave; th&agrave;nh phố trẻ năng động của tỉnh Quảng Ninh, những năm qua th&agrave;nh phố n&agrave;y c&oacute; tốc độ ph&aacute;t triển mạnh mẽ tr&ecirc;n tất cả c&aacute;c lĩnh vực. Trong đ&oacute;, phải kể đến:</p>\r\n<ul>\r\n	<li>\r\n		Tốc độ tăng trưởng kinh tế đạt 13%/năm 2017 cao nhất từ trước đến nay</li>\r\n	<li>\r\n		Quy hoạch giao th&ocirc;ng &ndash; hạ tầng cơ sở ng&agrave;y c&agrave;ng ho&agrave;n thiện v&agrave; đồng bộ</li>\r\n	<li>\r\n		Thế mạnh về du lịch t&acirc;m linh &ndash; sinh th&aacute;i với nổi bật l&agrave; khu di t&iacute;ch Y&ecirc;n Tử, ch&ugrave;a Ba V&agrave;ng, ch&ugrave;a Hang Son, Khu du lịch sinh th&aacute;i Hồ Y&ecirc;n Trung, Lựng Xanh&hellip;</li>\r\n</ul>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Tổng quan dự án Uông Bí New City\" src=\"/public/upload/images/uong-bi-new-city/tam-thanh-view-cong-vien.png\" /></p>\r\n<p>\r\n	Đặc biệt với trị tr&iacute; l&agrave; cửa ng&otilde; ph&iacute;a T&acirc;y của Quảng Ninh, U&ocirc;ng B&iacute; sở hữu kết nối v&ugrave;ng:</p>\r\n<ul>\r\n	<li>\r\n		Thuận tiện đi c&aacute;c tỉnh Hải Ph&ograve;ng, H&agrave; Nam, Hưng Y&ecirc;n, Bắc Ninh, Nam Định qua đường 10 v&agrave; đường 5</li>\r\n	<li>\r\n		Hai đầu th&agrave;nh phố lại tiếp gi&aacute;p với hai nền kinh tế năng động l&agrave; Thị x&atilde; Đ&ocirc;ng Triều v&agrave; Thị x&atilde; Quảng Y&ecirc;n, đem lại cho U&ocirc;ng B&iacute; nhiều động lực trong ph&aacute;t triển kinh tế v&agrave; tiềm năng đầu tư bất động sản.</li>\r\n	<li>\r\n		Được c&aacute;c chuy&ecirc;n gia bất động sản đ&aacute;nh gi&aacute; l&agrave; thị trường bất động sản lớn thứ hai Quảng Ninh, chỉ sau Hạ Long.</li>\r\n</ul>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Tổng quan dự án Uông Bí New City\" src=\"/public/upload/images/uong-bi-new-city/tam-thanh-truong-hoc.png\" /></p>\r\n<p>\r\n	<em><u>Tổng quan dự &aacute;n</u></em></p>\r\n<ul>\r\n	<li>\r\n		T&ecirc;n dự &aacute;n:&nbsp; U&ocirc;ng B&iacute; New City</li>\r\n	<li>\r\n		Vị tr&iacute;: Phường Y&ecirc;n Thanh, Th&agrave;nh phố U&ocirc;ng B&iacute;, tỉnh Quảng Ninh</li>\r\n	<li>\r\n		Chủ đầu tư: C&ocirc;ng ty Cổ phần Đầu tư X&acirc;y dựng v&agrave; Thương mại T&acirc;n Th&agrave;nh</li>\r\n	<li>\r\n		Tổng diện t&iacute;ch: 27,56 ha</li>\r\n	<li>\r\n		Tổng diện t&iacute;ch đất ở: 91,684 (33,26%)</li>\r\n	<li>\r\n		Đất c&acirc;y xanh: 10,215m2</li>\r\n	<li>\r\n		B&atilde;i đỗ xe: 3,570m2</li>\r\n	<li>\r\n		Khu sinh hoạt cộng đồng: 2,350m2</li>\r\n	<li>\r\n		Đất giao th&ocirc;ng, kỹ thuật: 109,461m2</li>\r\n	<li>\r\n		Quy m&ocirc;: 716 l&ocirc; Liền kề, Biệt thự, Shophouse diện t&iacute;ch từ 82 m2 &ndash; 366m2</li>\r\n	<li>\r\n		Đơn vị tư vấn thiết kế: C&ocirc;ng ty Cổ phần tư vấn Licogi</li>\r\n</ul>','<p>\r\n	Dự &aacute;n U&ocirc;ng B&iacute; New City tọa lạc tại khu đất v&agrave;ng trung t&acirc;m th&agrave;nh phố U&ocirc;ng B&iacute;, nằm tại khu vực ph&iacute;a nam ng&atilde; ba giao giữa Quốc lộ 18A v&agrave; đường Trần Nh&acirc;n T&ocirc;ng thuộc phường Y&ecirc;n Thanh.</p>\r\n<ul>\r\n	<li>\r\n		Ph&iacute;a Đ&ocirc;ng gi&aacute;p C&ocirc;ng ty TNHH Đức Lợi, khu d&acirc;n cư v&agrave; đất n&ocirc;ng nghiệp</li>\r\n	<li>\r\n		Ph&iacute;a T&acirc;y gi&aacute;p khu đ&ocirc; thị C&ocirc;ng Th&agrave;nh v&agrave; khu d&acirc;n cư</li>\r\n	<li>\r\n		Ph&iacute;a Nam gi&aacute;p khu d&acirc;n cư hiện trạng v&agrave; đất n&ocirc;ng nghiệp</li>\r\n	<li>\r\n		Ph&iacute;a Bắc gi&aacute;p Quốc lộ 18A</li>\r\n</ul>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Vị trí dự án Uông Bí New City\" src=\"/public/upload/images/uong-bi-new-city/vi-tri-tan-thanh-uong-bi-new-city.png\" /></p>\r\n<p>\r\n	Sở hữu vị tr&iacute; ho&agrave;n hảo được đ&aacute;nh gi&aacute; l&agrave; &ldquo;tr&aacute;i tim&rdquo; của th&agrave;nh phố U&ocirc;ng B&iacute;, dự &aacute;n U&ocirc;ng B&iacute; New City &ocirc;m trọn trung t&acirc;m thương mại Vincom+ v&agrave; nằm trong khu vực c&oacute; mật độ d&acirc;n cư đ&ocirc;ng đ&uacute;c, sầm uất với nhịp sống năng đ&ocirc;ng bậc nhất th&agrave;nh phố. Chỉ trong v&ograve;ng b&aacute;n k&iacute;nh 2km, dự &aacute;n c&oacute; thể kết nối thuận tiện đến c&aacute;c tiện &iacute;ch nổi bật của TP U&ocirc;ng B&iacute; như:</p>\r\n<ul>\r\n	<li>\r\n		Trường học: Trường cấp 1,2 Y&ecirc;n Thanh, Trường cao đẳng nghề Than &ndash; Kho&aacute;ng sản Quảng Ninh</li>\r\n	<li>\r\n		Bệnh viện: Bệnh viện đa khoa Việt Nam &ndash; Thụy Điển</li>\r\n	<li>\r\n		Trung t&acirc;m h&agrave;nh ch&iacute;nh &ndash; Văn h&oacute;a &ndash; Kinh tế: UBND phường Y&ecirc;n Thanh, CAND phường Y&ecirc;n Thanh, Ng&atilde; ba đường 10, Bến xe kh&aacute;ch, Ng&acirc;n h&agrave;ng, vườn hoa&hellip;</li>\r\n	<li>\r\n		Trung t&acirc;m thương mại: Liền kề Vincom+, Chợ đầu mối Y&ecirc;n Thanh, Chợ Thanh Sơn&hellip;</li>\r\n</ul>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"Vị trí dự án Uông Bí New City\" src=\"/public/upload/images/uong-bi-new-city/vi-tri-ket-noi-tan-thanh-uong-bi.jpg\" /></p>','<p>\r\n	Quy hoạch v&agrave; ph&aacute;t triển đồng bộ tr&ecirc;n tổng diện t&iacute;ch khoảng 27,56 ha thuộc Phường Y&ecirc;n Thanh, Th&agrave;nh Phố U&ocirc;ng B&iacute;, tỉnh Quảng Ninh, dự &aacute;n U&ocirc;ng B&iacute; New City được x&acirc;y dựng tr&ecirc;n &yacute; tưởng một đ&ocirc; thị kiểu mẫu hiện đại, cộng đồng d&acirc;n cư cao cấp, m&ocirc;i trường sống văn minh, l&yacute; tưởng khi quy tụ h&agrave;ng loạt c&aacute;c tiện &iacute;ch dịch vụ chất lượng như:</p>\r\n<ul>\r\n	<li>\r\n		C&ocirc;ng vi&ecirc;n c&acirc;y xanh</li>\r\n	<li>\r\n		Khu sinh hoạt cộng đồng</li>\r\n	<li>\r\n		B&atilde;i đỗ xe</li>\r\n	<li>\r\n		Trung t&acirc;m thương mại si&ecirc;u thị</li>\r\n	<li>\r\n		Kh&aacute;ch sạn, văn ph&ograve;ng</li>\r\n	<li>\r\n		Trường THCS, trường mẫu gi&aacute;o, nh&agrave; văn h&oacute;a</li>\r\n	<li>\r\n		Đường trải nhựa rộng, tho&aacute;ng.&nbsp;</li>\r\n</ul>\r\n<p>\r\n	Với hơn 60% diện t&iacute;ch d&agrave;nh cho c&ocirc;ng vi&ecirc;n c&acirc;y xanh, cảnh quan v&agrave; c&aacute;c tiện &iacute;ch c&ocirc;ng cộng, Dự &aacute;n U&ocirc;ng B&iacute; New City xứng đ&aacute;ng l&agrave; lựa chọn l&yacute; tưởng cho một chốn an cư b&igrave;nh y&ecirc;n với kh&ocirc;ng gian sống xanh, tiện nghi ho&agrave;n hảo m&agrave; ai cũng muốn trở về sau những x&ocirc; bồ, ồn &atilde; của cuộc sống.</p>\r\n<p>\r\n	<strong><em><u>Khu đ&ocirc; thị đ&aacute;ng sống bậc nhất tại U&ocirc;ng B&iacute;</u></em></strong></p>\r\n<p>\r\n	L&agrave; dự &aacute;n c&oacute; tổng mức đầu tư x&acirc;y dựng hạ tầng đ&ocirc; thị lớn nhất của TP U&ocirc;ng B&iacute; t&iacute;nh đến thời điểm hiện tại, U&ocirc;ng B&iacute; New City được quy hoạch ph&aacute;t triển một c&aacute;ch b&agrave;i bản, tiện &iacute;ch kh&eacute;p k&iacute;n, hạ tầng đồng bộ với c&aacute;c ph&acirc;n khu chức năng gồm khu Liền kề, biệt thự, shophouse, khu cao tầng, văn ph&ograve;ng, trung t&acirc;m thương mại, b&atilde;i đỗ xe, trường học, nh&agrave; văn h&oacute;a, khu sinh hoạt cộng đồng v&agrave; c&ocirc;ng vi&ecirc;n c&acirc;y xanh&hellip;</p>\r\n<p>\r\n	Đặc biệt, đường nội khu tại dự &aacute;n được trải đường nhựa, tho&aacute;ng rộng, vỉa h&egrave; l&aacute;t gạch được bố tr&iacute; cảnh quan c&acirc;y xanh, mạng lưới đ&egrave;n đường, hệ thống cấp tho&aacute;t nước&hellip;đều đ&atilde; ho&agrave;n thiện, khang trang, ph&aacute;p l&yacute; đẩy đủ, sổ đỏ l&acirc;u d&agrave;i. U&ocirc;ng B&iacute; New City xứng đ&aacute;ng l&agrave; khu đ&ocirc; thị c&oacute; kh&ocirc;ng gian sống văn minh, hiện đại, đ&aacute;p ứng mọi nhu cầu thiết thực nhất của cuộc sống.</p>','<p>\r\n	<img alt=\"\" src=\"/public/upload/files/m%E1%BA%B7t%20b%E1%BA%B1ng%20m%C3%A0u.jpg\" style=\"width: 848px; height: 1200px;\" /></p>',NULL,'<p>\r\n	Dự &aacute;n đang được chủ đầu tư gấp r&uacute;t ho&agrave;n thiện cơ sở hạ tầng trước khi ch&iacute;nh thức mở b&aacute;n v&agrave;o ng&agrave;y 23/12/2018 tới đ&acirc;y.&nbsp;</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/tin-tuc/ti%E1%BA%BFn%20%C4%91%E1%BB%99%20t%C3%A2n%20thanh/b3a5008b70d89386cac9.jpg\" style=\"width: 1280px; height: 960px;\" /></p>\r\n<p>\r\n	Chi tiết tiến độ ho&agrave;n thiện <a href=\"http://bdsquangninh.com/tien-do-du-an-uong-bi-new-city/60.html\"><strong><span style=\"color:#ff0000;\">xem tại đ&acirc;y</span></strong></a></p>',NULL,'Tổng quan dự án Uông Bí New City','Vị trí dự án Uông Bí New City','Tiện ích dự án Uông Bí New City','Mặt Bằng tổng thể dự án',NULL,'Tiến độ hoàn thiện dự án Uông Bí New City',NULL,NULL,'Uông Bí New City - Cơ hội đầu tư cự lớn tại trung tâm Uông Bí','13-12-2018','21-12-2018','uong-bi-new-city','Uông Bí là thành phố trẻ năng động của tỉnh Quảng Ninh, những năm qua thành phố này có tốc độ phát triển mạnh mẽ trên tất cả các lĩnh vực. Trong đó',3,'Uông Bí New City','0'),(74,33,22,NULL,'FLC Tropical City','Tầng 4 tòa nhà Việt Á Bank, 158 Lê Thánh Tông, Hạ Long, Quảng Ninh',NULL,NULL,NULL,NULL,1027,'/public/upload/images/FLC%20tropical/b%C3%ACa.png','11 triệu/m2',NULL,1,'1','<p>\r\n	<strong>T&ecirc;n dự &aacute;n</strong>: FLC Tropical City Hạ Long</p>\r\n<p>\r\n	&ndash; <strong>Vị tr&iacute;:</strong> Khu đ&ocirc; thị Cao Xanh, Phường H&agrave; Kh&aacute;nh, Th&agrave;nh phố Hạ Long</p>\r\n<p>\r\n	&ndash; <strong>Chủ đầu tư:</strong> Tập đo&agrave;n FLC Group</p>\r\n<p>\r\n	&ndash; <strong>Tổng diện t&iacute;ch dự &aacute;n</strong>: 87,426 ha</p>\r\n<p>\r\n	&ndash; <strong>Bao gồm :</strong> Nh&agrave; ở thấp tầng, chung cư, trường học, cảnh quan c&acirc;y xanh, b&atilde;i đỗ xe&hellip;</p>\r\n<p>\r\n	&ndash;<strong> Diện t&iacute;ch x&acirc;y dựng : </strong>22,6 ha</p>\r\n<p>\r\n	&ndash;<strong> Mật độ x&acirc;y dựng to&agrave;n khu </strong>: 25,8 %</p>\r\n<p>\r\n	&ndash; <strong>Tổng số l&ocirc; liền kề,shophouse </strong>: 1209 c&oacute; DT từ 75m2 đến 105m2</p>\r\n<p>\r\n	&ndash; <strong>T&ocirc;̉ng s&ocirc;́ căn h&ocirc;̣:</strong> 1089 c&oacute; DT từ 45m2 đ&eacute;n 63m2</p>\r\n<p>\r\n	&ndash; <strong>Thời gian khởi c&ocirc;ng</strong>: Qu&yacute; IV năm 2018</p>\r\n<p>\r\n	&ndash;<strong> Thời gian ho&agrave;n th&agrave;nh: </strong>đang cập nhật</p>','<p>\r\n	Dự &aacute;n <a href=\"http://bdsquangninh.com/du-an-bds/flc-tropical-city/74.html\">FLC Tropical City</a> tọa lạc tại khu đ&ocirc; thị Cao Xanh, Phường H&agrave; Kh&aacute;nh, th&agrave;nh phố Hạ Long. Dự &aacute;n hội tụ đầy đủ yếu tố &rdquo;nhất cận thị, nhị cận giang&rdquo; nh&igrave;n ra s&ocirc;ng Cửa Lục v&agrave; s&ocirc;ng Diễn Vọng, tiếp gi&aacute;p tuyến đường huyết mạch kết nối Hạ Long v&agrave; c&aacute;c tỉnh ph&iacute;a Bắc như: quốc lộ 1B, quốc lộ 18, đường v&agrave;nh đai ph&iacute;a Bắc.&nbsp;<img alt=\"\" src=\"/public/upload/files/0-02-06-da1df36d60223863abb148c8446fdd0e80e4c2adefe320ea41f6a506917272f0_7553e805.jpg\" style=\"width: 1280px; height: 720px;\" /></p>','<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/FLC%20tropical/0534c2123294d7ca8e85.jpg\" style=\"width: 1080px; height: 548px;\" /></p>','<p>\r\n	<a href=\"http://bdsquangninh.com/flc-tropical-city/58.html\">FLC Tropical City</a> mang trong m&igrave;nh trọng tr&aacute;ch ph&aacute;t huy những gi&aacute; trị vốn c&oacute; của di sản thi&ecirc;n nhi&ecirc;n thế giới Vịnh Hạ Long. Ch&iacute;nh những y&ecirc;u cầu đ&oacute; m&agrave; chủ đầu tư đ&atilde; gửi gắm v&agrave;o ng&ocirc;n ngữ thiết kế tổng quan của khu đ&ocirc; thị những kh&ocirc;ng gian xanh v&ocirc; gi&aacute;. Với mật độ x&acirc;y dựng to&agrave;n khu chỉ 25.8%, dự &aacute;n sẽ trở th&agrave;nh khu đ&ocirc; thị đ&aacute;ng sống bậc nhất tại Hạ Long trong tương lai.&nbsp;</p>\r\n<p>\r\n	<iframe allow=\"accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/3_RjoWoamig\" width=\"560\"></iframe></p>','<p>\r\n	FLC Tropical City Ha Long lấy cảm hứng từ những thi&ecirc;n đường nghỉ dưỡng nổi tiếng tr&ecirc;n thế giới, đ&oacute; l&agrave;:</p>\r\n<p>\r\n	-&nbsp; Bali Forest đặc trưng bởi kh&ocirc;ng gian sống s&ocirc;i động, trẻ trung mang.</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/FLC%20tropical/SDMB-BALI-FOREST.jpg\" style=\"width: 1600px; height: 1132px;\" /></p>\r\n<p>\r\n	- Botanic Garden như &ldquo;l&aacute; phổi xanh&rdquo; trong l&agrave;nh của to&agrave;n khu đ&ocirc; thị với những c&ocirc;ng vi&ecirc;n c&acirc;y xanh c&oacute; một kh&ocirc;ng hai.</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/FLC%20tropical/M%E1%BA%B7t-b%E1%BA%B1ng-FLC-TROPICAL-CITY-1.jpg\" style=\"width: 1600px; height: 1082px;\" /></p>\r\n<p>\r\n	- Palm Village với những d&atilde;y cọ thẳng tắp trải d&agrave;i như một biểu tượng của Hạ Long thu nhỏ.</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/FLC%20tropical/togap2.jpg\" style=\"width: 1080px; height: 466px;\" /></p>\r\n<p>\r\n	- Hawaii Seaside với kh&ocirc;ng gian sống hiện đại, sang trọng c&ugrave;ng những dịch vụ tiện &iacute;ch đồng bộ đẳng cấp bậc nhất.</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/FLC%20tropical/FILE%20VIEW2.jpg\" style=\"width: 1080px; height: 350px;\" /></p>',NULL,'<p>\r\n	<img alt=\"\" src=\"/public/upload/images/FLC%20tropical/29_1.jpg\" style=\"width: 1023px; height: 1200px;\" /><img alt=\"\" src=\"/public/upload/images/FLC%20tropical/29.jpg\" style=\"width: 1200px; height: 1034px;\" /></p>','<ul>\r\n	<li>\r\n		<span style=\"color:#ff0000;\">Chiết khấu 3% gi&aacute; trị l&ocirc; đất khi kh&aacute;ch h&agrave;ng kh&ocirc;ng vay vốn ng&acirc;n h&agrave;ng.</span></li>\r\n	<li>\r\n		<span style=\"color:#ff0000;\">Chiết khấu&nbsp; 01% cho kh&aacute;ch h&agrave;ng đặt cọc th&agrave;nh c&ocirc;ng 3-5 căn.</span></li>\r\n	<li>\r\n		<span style=\"color:#ff0000;\">Chiết khấu&nbsp; 02% cho kh&aacute;ch h&agrave;ng đặt cọc th&agrave;nh c&ocirc;ng 6 căn trở l&ecirc;n.</span></li>\r\n	<li>\r\n		<span style=\"color:#ff0000;\">Kh&aacute;ch h&agrave;ng thanh to&aacute;n sớm được hưởng chiết khấu th&ecirc;m 12%/năm.</span></li>\r\n</ul>','Tổng quan dự án FLC Tropical City Hạ Long','Vị Trí dự án.','Các tiện ích của dự án','Mặt bằng phân khu dự án',NULL,'Tiến độ đóng tiền.','Chính Sách bán hàng FLC Tropical City',NULL,'FLC Tropical - Mảnh đất vàng cuối cùng sót lại tại Hạ Long','17-12-2018','31-5-2019','flc-tropical-city','FLC Tropical City siêu dự án của tập đoàn FLC với đất nền, shophouse, chung cư tọa lạc tại vị trí đất vàng Hạ Long cùng tiện ích xanh chưa từng có',4,'FLC Tropical City','0'),(75,24,22,NULL,'Condotel Trí Đức - Best Western Best Western Plus Ha Long Bay','Đối diện công Viên Sun World HaLong Park',NULL,NULL,NULL,NULL,736,'/public/upload/files/Tr%C3%AD%20%C4%90%E1%BB%A9c/16ff5a59122af274ab3b.jpg',NULL,NULL,1,'1','<p>\r\n	<span style=\"font-family:arial,helvetica,sans-serif;\">T&ecirc;n thương mại: Best Western Plus Ha Long Bay Hotel &ndash; Tr&iacute; Đức Hạ Long.</span></p>\r\n<p>\r\n	<span style=\"font-family:arial,helvetica,sans-serif;\">Chủ đầu tư: Cty Cổ phần Kh&aacute;ch sạn Tr&iacute; Đức.<br />\r\n	Vị tr&iacute;: khu 4, đường Hạ Long, phường B&atilde;i Ch&aacute;y, Hạ Long, Quảng Ninh &ndash; đối diện Sun World Hạ Long Park<br />\r\n	Tổng diện t&iacute;ch: 2,901.58m2.<br />\r\n	Diện t&iacute;ch s&agrave;n: 3.768m2.<br />\r\n	Đơn vị quản l&yacute; &amp; vận h&agrave;nh: Tập đo&agrave;n Hilton &ndash; Hoa Kỳ.<br />\r\n	Tổng thầu x&acirc;y dụng: Coteccons.<br />\r\n	Loại h&igrave;nh: Tổ hợp căn hộ kh&aacute;ch sạn.<br />\r\n	Quy m&ocirc;: t&ograve;a nh&agrave; cao 35 tầng, c&oacute; 8 tầng dịch vụ v&agrave; 26 tầng căn hộ.<br />\r\n	Sản phẩm: Kh&aacute;ch sạn &amp; Căn hộ Condotel.<br />\r\n	Khởi c&ocirc;ng: 06/12/2017.<br />\r\n	Ho&agrave;n thiện &amp; đi v&agrave;o hoạt động: Năm 2020.</span></p>','<p>\r\n	<a href=\"http://bdsquangninh.com/du-an-bds/condotel-tri-duc-best-western-best-western-plus-ha-long-bay/75.html\">Dự &aacute;n Condotel Tr&iacute; Đức - Best Western Plus Ha Long Bay</a> l&agrave; dự &aacute;n c&oacute; vị tr&iacute; nằm tại trung t&acirc;m khu du lịch B&atilde;i Ch&aacute;y v&agrave; c&oacute; tiềm năng sinh lời tốt nhất...</p>\r\n<p>\r\n	Những năm gần đ&acirc;y Th&agrave;nh phố Hạ Long đang thu h&uacute;t rất nhiều những nh&agrave; đầu tư lớn như VinGroup, SunGroup, <a href=\"http://bdsquangninh.com/dat-nen/flc-tropical-city/74.html\">FLC Group</a>, Bim Group với những si&ecirc;u c&ocirc;ng tr&igrave;nh Shophouse, Boutique Shophouse, biệt thự nghỉ dưỡng, đất nền. Nhưng b&ecirc;n cạnh những c&ocirc;ng tr&igrave;nh đẳng cấp ấy th&igrave; Hạ Long vẫn c&ograve;n thiếu đi một ph&acirc;n kh&uacute;c đầu tư kh&ocirc;ng thể bỏ lỡ l&agrave; Condotel. Vẫn c&ograve;n kh&aacute; mới mẻ đối với nh&agrave; đầu tư Hạ Long. Kh&ocirc;ng c&oacute; nhiều lựa chọn như Nha Trang, Đ&agrave; Nẵng tuy nhi&ecirc;n Condotel Hạ Long lại c&oacute; tiềm năng ph&aacute;t triển rất tốt trong tương lai. Condotel Tr&iacute; Đức ra đời như giải tỏa được cơn kh&aacute;t đầu tư Condotel tại Hạ Long cho nh&agrave; đầu tư.</p>\r\n<p>\r\n	Dự &aacute;n Condotel Tr&iacute; Đức ti&ecirc;u chuẩn 5 sao thiết kế th&ocirc;ng minh với 100% căn hộ hướng biển. Thiết kế căn hộ đa dạng mang đến cho kh&aacute;ch h&agrave;ng nhiều lựa chọn từ căn hộ 1 ph&ograve;ng ngủ - 2 ph&ograve;ng ngủ. Phong c&aacute;ch nội thất hiện đại đẳng cấp tinh tế đến từng chi tiết nhỏ, dự &aacute;n với sự nghỉ ngơi tuyệt đối cho kh&aacute;ch h&agrave;ng khi nghỉ dưỡng tại đ&acirc;y.<img alt=\"\" src=\"/public/upload/files/Tr%C3%AD%20%C4%90%E1%BB%A9c/44c6d87f900c7052291d.jpg\" style=\"width: 1562px; height: 873px;\" /></p>','<p>\r\n	<a href=\"http://bdsquangninh.com/du-an-bds/condotel-tri-duc-best-western-best-western-plus-ha-long-bay/75.html\">Condotel Tr&iacute; Đức - Best Western Plus Ha Long Bay</a> nằm tại vị tr&iacute; đắc địa bậc nhất tại Hạ Long. Dự &aacute;n nằm đối diện tổ hợp vui chơi Sun World Ha Long lớn nhất Đ&ocirc;ng Nam &Aacute; của tập đo&agrave;n Sun Group vừa được đưa v&agrave;o khai th&aacute;c năm 2017 thu h&uacute;t h&agrave;ng triệu lượt kh&aacute;ch thăm quan hằng năm. B&ecirc;n cạnh dự &aacute;n l&agrave; kh&aacute;ch sạn Mường Thanh 5 sao, trước mặt l&agrave; khu phố đi bộ sầm uất bậc nhất tại Hạ Long.</p>\r\n<p>\r\n	<img alt=\"Vị trí dự án Condotel Trí Đức \" src=\"/public/upload/files/Tr%C3%AD%20%C4%90%E1%BB%A9c/Vi-tri-Hillton-Ha-Long-Bay.jpg\" style=\"width: 1080px; height: 505px;\" /></p>','<p>\r\n	Condotel Tr&iacute; Đức mang trong m&igrave;nh t&acirc;m huyết của chủ đầu tư (C&ocirc;ng ty cổ phần Tr&iacute; Đức) n&ecirc;n cũng mang trong m&igrave;nh sự độc đ&aacute;o hiếm c&oacute;. Best Western Plus Ha Long Bay tất cả c&aacute;c căn đều c&oacute; tầm nh&igrave;n hướng vịnh đảm bảo cho nh&agrave; đầu tư đều sở hữu những gi&aacute; trị triệu đ&ocirc; đ&iacute;ch thực.&nbsp;<img alt=\"\" src=\"/public/upload/files/Tr%C3%AD%20%C4%90%E1%BB%A9c/DT_Isomau%201_7k.jpg\" style=\"width: 1080px; height: 670px; float: left;\" /></p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/Tr%C3%AD%20%C4%90%E1%BB%A9c/DT_Isomau%204_7k.jpg\" style=\"height: 670px; width: 1080px;\" /></p>\r\n<p>\r\n	&nbsp;</p>','<p>\r\n	Condotel Tr&iacute; Đức mang trong m&igrave;nh những gi&aacute; trị về tiềm năng đầu tư bất động sản kh&ocirc;ng thể bỏ lỡ tại trung t&acirc;m khu du lịch B&atilde;i Ch&aacute;y. C&ugrave;ng với quỹ căn hạn chế chỉ 432 căn hộ 1 ph&ograve;ng ngủ v&agrave; 2 ph&ograve;ng ngủ được trang bị nội thất 5 sao theo chuẩn của đơn vị vận h&agrave;nh Best Western. Thiết kế v&agrave; chia căn linh hoạt, ban c&ocirc;ng rộng hướng vịnh đ&oacute;n gi&oacute;, diện t&iacute;ch ph&ugrave; hợp. Dự &aacute;n thực sự sẽ mang đến cho thị trường bất động sản tại Hạ Long những cơn s&oacute;ng thực sự.&nbsp;<img alt=\"mặt bằng điển hình dự án \" src=\"/public/upload/files/Tr%C3%AD%20%C4%90%E1%BB%A9c/8c4a3d6abe6a5e34077b.jpg\" style=\"width: 1191px; height: 841px;\" /></p>\r\n<p>\r\n	&nbsp;</p>','<p>\r\n	Dự &aacute;n Condotel Tr&iacute; Đức được vận h&agrave;nh bởi đơn vị uy t&iacute;n bậc nhất tr&ecirc;n thế giới Best Western. Tập đo&agrave;n Best Western th&agrave;nh lập từ năm 1946 đến nay tập đo&agrave;n đang vận h&agrave;nh hơn 4.100 kh&aacute;ch sạn c&ugrave;ng với hơn 2.000 nh&acirc;n c&ocirc;ng tr&ecirc;n khắp thế giới. Mang trong m&igrave;nh phong c&aacute;ch nội thất cao cấp bậc nhất của tập đo&agrave;n Best Western, dự &aacute;n chắc chắn sẽ mang lại cho qu&yacute; nh&agrave; đầu tư cơ hội đầu tư kh&ocirc;ng thể bỏ lỡ.&nbsp;</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/Tr%C3%AD%20%C4%90%E1%BB%A9c/7c31b591fde21dbc44f3.jpg\" style=\"width: 1080px; height: 606px;\" /></p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/Tr%C3%AD%20%C4%90%E1%BB%A9c/06e38e53c620267e7f31.jpg\" style=\"width: 1080px; height: 814px;\" /></p>\r\n<p>\r\n	&nbsp;</p>','<p>\r\n	Condotel Tr&iacute; Đức ngo&agrave;i thiết kế nội thất sang trọng bậc nhất c&ograve;n mang trong m&igrave;nh những tiện &iacute;ch hiếm c&oacute; khiến cho gi&aacute; trị của dự &aacute;n kh&ocirc;ng ngừng được tăng cao.&nbsp;<img alt=\"Bể bơi vô cực view toàn vịnh Hạ Long\" src=\"/public/upload/files/Tr%C3%AD%20%C4%90%E1%BB%A9c/de6fb6dcfeaf1ef147be.jpg\" style=\"width: 1080px; height: 607px;\" /><img alt=\"Sky Bar cao nhất Hạ Long\" src=\"/public/upload/files/Tr%C3%AD%20%C4%90%E1%BB%A9c/b7b5afe9e79a07c45e8b.jpg\" style=\"width: 1080px; height: 607px;\" /><img alt=\"Tất cả các căn hộ đều có View Vịnh Hạ Long\" src=\"/public/upload/files/Tr%C3%AD%20%C4%90%E1%BB%A9c/7034dc9694e574bb2df4.jpg\" style=\"width: 1080px; height: 605px;\" /><img alt=\"Khu vui chơi trẻ em\" src=\"/public/upload/files/Tr%C3%AD%20%C4%90%E1%BB%A9c/90dd4e650616e648bf07.jpg\" style=\"width: 1080px; height: 606px;\" /><img alt=\"Nhà hàng sang trọng\" src=\"/public/upload/files/Tr%C3%AD%20%C4%90%E1%BB%A9c/00531bea5399b3c7ea88.jpg\" style=\"width: 1080px; height: 606px;\" /></p>','<p style=\"color: red;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Cam kết chia sẻ lợi nhuận l&ecirc;n đến 85% - 15%&nbsp;</span></span></p>\r\n<p style=\"color: red;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Cam kết lợi nhuận 8% gi&aacute; trị căn hộ/ năm</span></span></p>\r\n<p style=\"color: red;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Chiết khấu l&ecirc;n đến 9.5%&nbsp;</span></span></p>\r\n<p style=\"color: red;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Hỗ trợ vay ng&acirc;n h&agrave;ng l&ecirc;n đến 45%&nbsp;</span></span></p>\r\n<p style=\"color: red;\">\r\n	<span style=\"font-size:18px;\"><span style=\"font-family:times new roman,times,serif;\">Thời gian hỗ trợ l&atilde;i suất đến khi b&agrave;n giao nh&agrave;.&nbsp;</span></span></p>\r\n<p>\r\n	&nbsp;</p>','Tổng quan dự án','Vị Trí dự án Condotel trí đức','Mặt Bằng thiết kế căn hộ','Măt bằng điển hình dự án','thiết kế dự án.','Tiện ích dự án','Chính sách bán hàng',NULL,'Condotel Trí Đức - Best Western Plus Ha Long Bay Vị trí kim cương bên vịnh Kỳ quan','25-12-2018','25-12-2018','condotel-tri-duc-best-western-best-western-plus-ha-long-bay','Dự án Condotel Trí Đức - Best Western Plus Ha Long Bay là dự án có vị trí nằm tại trung tâm khu du lịch Bãi Cháy và có tiềm năng sinh lời tốt nhất...',1,'Condotel Trí Đức','0'),(76,24,22,NULL,'Hometel Beverly Hills Hạ Long','Đồi Hải Quân, Bãi Cháy, Hạ Long',NULL,NULL,NULL,NULL,810,'/public/upload/files/Beverly%20Hills/beverly%20hills.jpg','1,4 tỉ/căn',NULL,0,'1','<p>\r\n	T&ecirc;n dự &aacute;n: Beverly Hills Hạ Long<br />\r\n	Vị tr&iacute;: Đồi Hải Qu&acirc;n, B&atilde;i Ch&aacute;y, Hạ Long<br />\r\n	Chủ đầu tư: Đức Dương Group<br />\r\n	Đơn vị tư vấn thiết kế: C&ocirc;ng ty Cổ phần kiến tr&uacute;c v&agrave; quy hoạch đ&ocirc; thị H&agrave; Nội<br />\r\n	Đơn vị thi c&ocirc;ng: C&ocirc;ng ty Cổ phần ph&aacute;t triển đầu tư x&acirc;y dựng Việt Nam (Amacao)<br />\r\n	Tổng diện t&iacute;ch đất: 10,8ha<br />\r\n	Mật độ x&acirc;y dựng: 40%<br />\r\n	Loại h&igrave;nh sản phẩm: Hometel, chung cư, biệt thự, liền kề<br />\r\n	Tổng số căn Hometel: 160 căn gồm 17 tầng nổi v&agrave; 2 tầng khối đế d&agrave;nh cho tiện &iacute;ch cao cấp: thương mại, Spa, si&ecirc;u thị&hellip;</p>\r\n<p>\r\n	Diện t&iacute;ch: 35m2 &ndash; 62m2<br />\r\n	B&agrave;n giao: Dự kiến qu&yacute; I/2020</p>','<p>\r\n	<a href=\"http://bdsquangninh.com/du-an-bds/hometel-beverly-hills-ha-long/76.html\">Dự &aacute;n Beverly Hills Hạ Long</a> l&agrave; tổ hợp dự &aacute;n Hometel, Biệt thự, kh&aacute;ch sạn, chung cư mang trong m&igrave;nh phong c&aacute;ch thiết kế Địa Trung Hải tr&agrave;n ngập &aacute;nh s&aacute;ng tự nhi&ecirc;n. Dự &aacute;n do chủ đầu tư Đức Dương l&agrave; một trong những c&ocirc;ng ty uy t&iacute;n trong lĩnh vực bất động sản v&agrave; đ&atilde; được thị trường đ&oacute;n nhận nhiều năm tr&ecirc;n thị trường sẽ mang đến cho kh&aacute;ch h&agrave;ng những sản phẩm bất động sản chất lượng v&agrave; một kh&ocirc;ng gian sống đẳng cấp.&nbsp;<img alt=\"phối cảnh tổng thể dự án Beverly Hills Hạ Long\" src=\"/public/upload/files/Beverly%20Hills/b159f93a35efcdb194fe.jpg\" style=\"width: 1279px; height: 723px;\" /></p>','<p>\r\n	Dự &aacute;n Beverly Hills Hạ Long tọa lạc nơi đỉnh đồi Đức Dương điểm cao nhất B&atilde;i Ch&aacute;y với tầm nh&igrave;n 4 mặt ra Vịnh Hạ Long xinh đẹp, Từ dự &aacute;n kh&aacute;ch h&agrave;ng c&oacute; thể thu trọn Vịnh Hạ Long, Cầu B&atilde;i Ch&aacute;y, v&ograve;ng quay Mặt Trời, vịnh Cửa Lục trong tầm mắt.&nbsp;</p>\r\n<p>\r\n	<img alt=\"Vị trí dự án Beverly Hills Hạ Long\" src=\"/public/upload/files/Beverly%20Hills/302233b55e26be78e737.jpg\" style=\"width: 825px; height: 640px;\" /></p>','<p>\r\n	Dự &aacute;n Beverly Hills Hạ Long sở hữu trong m&igrave;nh 138 căn biệt thự cao cấp với diện t&iacute;ch từ 240 m2 &ndash; 400 m2; 22 L&ocirc; Liền kề, diện t&iacute;ch từ 75m2 &ndash; 139m2, 2 th&aacute;p cao 18 tầng (CT1: căn hộ v&agrave; CT2: Hometel). Với đầy đủ c&aacute;c tiện &iacute;ch của 1 khu đ&ocirc; thị đ&aacute;ng sống bậc nhất như: Bể bơi ngo&agrave;i trời rộng gần 1000m2, Khu trung t&acirc;m thể thao, vui chơi giải tr&iacute; phức hợp, Nh&agrave; h&agrave;ng cao cấp, Kh&aacute;ch sạn sang trọng, Trường mầm non ti&ecirc;u chuẩn,&nbsp; Khu văn ph&ograve;ng, Sky Bar cao nhất Hạ Long tầng 19, Khu sinh hoạt cộng đồng, Casino. Với những tiện &iacute;ch vượt trội như tr&ecirc;n, dự &aacute;n chắc chắn sẽ mang đến cho kh&aacute;ch h&agrave;ng kh&ocirc;ng gian sống đẳng cấp.&nbsp;</p>\r\n<p>\r\n	<img alt=\"tiện ích dự án Beverly Hills Hạ Long\" src=\"/public/upload/files/Beverly%20Hills/5bada6614de3adbdf4f2.jpg\" style=\"width: 825px; height: 825px;\" /></p>','<p>\r\n	Mặt bằng dự &aacute;n được đơn vị thiết kế linh hoạt với nhiều diện t&iacute;ch linh hoạt ph&ugrave; hợp với từng nhu cầu kh&aacute;ch h&agrave;ng.&nbsp;</p>\r\n<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width: 500px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<input alt=\"\" src=\"/public/upload/files/Beverly%20Hills/1.jpg\" style=\"width: 600px; height: 286px;\" type=\"image\" /></td>\r\n			<td>\r\n				<input alt=\"\" src=\"/public/upload/files/Beverly%20Hills/2.jpg\" style=\"width: 600px; height: 290px;\" type=\"image\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<input alt=\"\" src=\"/public/upload/files/Beverly%20Hills/2.jpg\" style=\"width: 600px; height: 290px;\" type=\"image\" /></td>\r\n			<td>\r\n				<input alt=\"\" src=\"/public/upload/files/Beverly%20Hills/4.jpg\" style=\"width: 600px; height: 288px;\" type=\"image\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<input alt=\"\" src=\"/public/upload/files/Beverly%20Hills/5.jpg\" style=\"width: 600px; height: 289px;\" type=\"image\" /></td>\r\n			<td>\r\n				<input alt=\"\" src=\"/public/upload/files/Beverly%20Hills/1.jpg\" style=\"width: 600px; height: 286px;\" type=\"image\" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>','<p>\r\n	C&aacute;c căn hộ Homtel được chủ đ&acirc;u tư Đức Dương d&agrave;nh nhiều t&acirc;m huyết cho thiết kế nội thất với phong c&aacute;ch miền Địa Trung Hải nhiều gam m&agrave;u s&aacute;ng đan xem. Căn hộ được đơn vị thiết kế đ&oacute;n tối ưu &aacute;nh s&aacute;ng tự nhi&ecirc;n mang lại kh&ocirc;ng gian sống năng động trẻ trung nhưng cũng kh&ocirc;ng k&eacute;m phần sang trọng cho chủ sở hữu.&nbsp;</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/Beverly%20Hills/view1.jpg\" style=\"width: 1080px; height: 720px;\" /><img alt=\"\" src=\"/public/upload/files/Beverly%20Hills/view5.jpg\" style=\"width: 1080px; height: 720px;\" /></p>','<ul>\r\n	<li>\r\n		<span style=\"color:#0000ff;\"><strong><span style=\"font-size:16px;\">Chiết khấu 2% khi lựa chọn thanh to&aacute;n 95%</span></strong></span></li>\r\n	<li>\r\n		<span style=\"color:#0000ff;\"><strong><span style=\"font-size:16px;\">Nhận cam kết lợi nhuận ngay khi đ&oacute;ng 95% gi&aacute; b&aacute;n Hometel, thanh to&aacute;n 6 th&aacute;ng/lần</span></strong></span></li>\r\n	<li>\r\n		<span style=\"color:#0000ff;\"><strong><span style=\"font-size:16px;\">Nhận lợi nhuận 12%/năm/gi&aacute; trị Hometel</span></strong></span></li>\r\n	<li>\r\n		<span style=\"color:#0000ff;\"><strong><span style=\"font-size:16px;\">Cam kết mua lại 100% gi&aacute; trị Hometel sau 5 năm.&nbsp;</span></strong></span></li>\r\n	<li>\r\n		<span style=\"color:#0000ff;\"><strong><span style=\"font-size:16px;\">Tặng 10 đ&ecirc;m nghỉ dưỡng miễn ph&iacute;/ năm tại kh&aacute;ch sạn.&nbsp;</span></strong></span></li>\r\n</ul>','<p>\r\n	<span style=\"font-size:20px;\"><span style=\"color: rgb(255, 0, 0);\"><strong>1. Kh&aacute;ch h&agrave;ng thanh to&aacute;n 95% gi&aacute; trị Hometel được nhận chiết khấu 2% gi&aacute; trị hometel.</strong></span></span></p>\r\n<p>\r\n	<span style=\"font-size:20px;\"><strong style=\"color: rgb(255, 0, 0); font-size: 18px;\">2. Kh&aacute;ch h&agrave;ng thanh to&aacute;n theo tiến độ.</strong></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"/public/upload/files/Beverly%20Hills/dddad0abb6704e2e1761.jpg\" style=\"width: 700px; height: 351px;\" /></p>','Tổng quan dự án Beverly Hills','Vị trí dự án Beverly Hills Hạ Long','Tiện ích dự án Beverly Hills Hạ Long','Mặt bằng thiết kế dự án.','Thiết kế nội thất Hometel Beverly Hills Hạ Long','Chính sách bán hàng dự án','Tiến độ đóng tiền dự án',NULL,'Hometel Beverly Hills Hạ Long - Lợi nhuận lên đến 12%/năm','27-12-2018','8-2-2020','hometel-beverly-hills-ha-long','Dự án Hometel Beverly Hills Hạ Long - do Đức Dương Group là chủ đầu tư, tọa lạc tại đỉnh đồi Hải Quân, Bãi Cháy, Hạ Long với 3 mặt View biển...',7,'Dự án Beverly Hills Hạ Long','0'),(77,24,22,NULL,'The Sapphire Residence Hạ Long','Bến Đoan, Hồng Gai',NULL,NULL,NULL,NULL,521,'/public/upload/files/Sapphire/f04b6f5657a0b6feefb1.jpg','1,4 tỷ/căn',NULL,0,'1',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'The Sapphire Residence Hạ Long - Thiên đường sống xanh bên bờ vịnh biếc','27-12-2018',NULL,'the-sapphire-residence-ha-long','Dự án Căn hộ cao cấp The Sapphire Residence Hạ Long do tập đoàn Doji làm chủ đầu tư. Chung cư The Sapphire tọa lạc vị trí Kim cương của vịnh Hạ Long..',0,'The sapphire Residence Hạ Long','0'),(78,24,23,NULL,'Dự án khu đô thị Quảng Hồng - Cẩm Phả',NULL,NULL,NULL,NULL,NULL,1228,'/public/upload/images/QU%E1%BA%A3ng%20H%E1%BB%93ng/51982138_568319620245486_842199282374148096_n.jpg',NULL,NULL,1,'1','<p>\r\n	Chủ đầu tư: C&ocirc;ng ty TNHH X&acirc;y Dựng Quảng Hồng</p>\r\n<p>\r\n	Địa chỉ: QL18, Cẩm Đ&ocirc;ng, Cẩm Phả</p>\r\n<p>\r\n	Nh&agrave; thầu x&acirc;y dựng: C&ocirc;ng ty TNHH X&acirc;y Dựng Quảng Hồng</p>\r\n<p>\r\n	Tổng diện t&iacute;ch: 271.4 ha</p>\r\n<p>\r\n	Giai đoạn 1: 14.4 ha gồm: Khu liền kề, Biệt Thự, Hồ điều h&ograve;a&hellip;</p>\r\n<p>\r\n	Giai Đoạn 2: 87 ha gồm: Nh&agrave; liền kề, Biệt Thự, B&atilde;i Tắm Quảng Hồng, Cảng t&agrave;u du lịch&hellip;</p>\r\n<p>\r\n	Giai đoạn 3: 170 ha gồm Khu Dịch vụ du lịch Quảng Hồng: Khu vui chơi cảm gi&aacute;c mạnh, Kh&aacute;ch sạn Quốc tế, Resort, Thủy cung, Nh&agrave; thi đấu&hellip;</p>','<p>\r\n	Dự &aacute;n khu đ&ocirc; thị Quảng Hồng nằm s&aacute;t cạnh quốc lộ 18A thuộc địa b&agrave;n phường Cẩm Sơn th&agrave;nh phố Cẩm Phả, b&ecirc;n trong dự &aacute;n l&agrave; b&atilde;i tắm Quảng Hồng, b&atilde;i tắm ch&iacute;nh của người d&acirc;n Cẩm Phả.&nbsp;</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/QU%E1%BA%A3ng%20H%E1%BB%93ng/c034bc825eeebcb0e5ff%20(1).jpg\" style=\"width: 1280px; height: 1002px;\" /></p>','<p>\r\n	<img alt=\"\" src=\"/public/upload/images/QU%E1%BA%A3ng%20H%E1%BB%93ng/5a6d57978fec6db234fd.jpg\" style=\"width: 1080px; height: 509px;\" /></p>','<p>\r\n	B&atilde;i Tắm Quảng Hồng.&nbsp;</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/QU%E1%BA%A3ng%20H%E1%BB%93ng/827883c861a483fadab5%20(1).jpg\" style=\"width: 960px; height: 540px;\" /><img alt=\"\" src=\"/public/upload/images/QU%E1%BA%A3ng%20H%E1%BB%93ng/b97441c4a3a841f618b9%20(1).jpg\" style=\"width: 960px; height: 540px;\" /></p>','<p>\r\n	Dự &aacute;n đ&atilde; ho&agrave;n thiện giai đoạn 1, đ&atilde; b&agrave;n giao đất v&agrave; sổ đỏ cho người d&acirc;n v&agrave; h&igrave;nh th&agrave;nh đơn vị ở với lượng d&acirc;n cư sinh sống tấp nập. Hiện tại chủ đầu tư l&agrave;&nbsp;C&ocirc;ng ty TNHH X&acirc;y Dựng Quảng Hồng đang t&iacute;ch cực thi c&ocirc;ng giai đoạn 2 của dự &aacute;n để đưa v&agrave;o khai th&aacute;c trong thời gian sớm nhất.&nbsp;</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/QU%E1%BA%A3ng%20H%E1%BB%93ng/51934386_568319676912147_7347527164608118784_n.jpg\" style=\"width: 960px; height: 720px;\" /><img alt=\"\" src=\"/public/upload/images/QU%E1%BA%A3ng%20H%E1%BB%93ng/51920766_568319650245483_7647435900435038208_n.jpg\" style=\"width: 960px; height: 720px;\" /></p>','<p>\r\n	<img alt=\"\" src=\"/public/upload/images/QU%E1%BA%A3ng%20H%E1%BB%93ng/%E1%BA%A3nh%20b%C3%ACa%20%C4%91%E1%BB%8F.PNG\" style=\"width: 875px; height: 603px;\" /></p>',NULL,NULL,'Mặt bằng dự án','Vị Trí dự án','các tiện ích của dự án','Tiến độ dự án.','Sổ đỏ',NULL,NULL,NULL,'Dự án đất nền Quảng Hồng - Cẩm Phả','22-2-2019','22-2-2019','du-an-khu-do-thi-quang-hong-cam-pha','Dự án khu đô thị Quảng Hồng - Cẩm Phả nằm ngay cạnh bãi tắm Quảng Hồng, bãi tắm chính của người dân Cẩm Phả.',41,'Dự án Quảng Hồng','0'),(79,24,23,NULL,'Green Park Cẩm Phả - Trái tim mới của Cẩm Phả','Vincom Cẩm Phả',NULL,NULL,NULL,NULL,609,'/public/upload/files/Green%20Park%20C%E1%BA%A9m%20Ph%E1%BA%A3/8cb1e5bcc25827067e49.jpg',NULL,NULL,1,'1','<p>\r\n	<a href=\"http://bdsquangninh.com/du-an-bds/green-park-cam-pha-trai-tim-moi-cua-cam-pha/79.html\">Green park Cẩm phả</a> &ldquo;Vị tr&iacute; tr&aacute;i tim&rdquo;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	T&ecirc;n dự &aacute;n: khu đ&ocirc; thị Green park Cẩm Phả</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Chủ đầu tư: C&ocirc;ng ty TNHH T&acirc;m Th&agrave;nh</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Địa chỉ: Trung t&acirc;m TP Cẩm Phả, ngay cạnh Vincom Plaza - phường Cẩm B&igrave;nh - TP. Cẩm Phả - Quảng Ninh</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Đơn vị ph&acirc;n phối dự &aacute;n: Tập đo&agrave;n Đất Xanh</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Tổng diện t&iacute;ch&nbsp; đất quy hoạch tự x&acirc;y: 3,6ha</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Loại h&igrave;nh sản phẩm: đất nền tự x&acirc;y</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Tổng số l&ocirc; liền kề: 165 l&ocirc;</p>','<p>\r\n	T&ecirc;n dự &aacute;n: khu đ&ocirc; thị <a href=\"http://bdsquangninh.com/du-an-bds/green-park-cam-pha-trai-tim-moi-cua-cam-pha/79.html\">Green park Cẩm Phả</a><br />\r\n	Chủ đầu tư: C&ocirc;ng ty TNHH T&acirc;m Th&agrave;nh<br />\r\n	Địa chỉ: trung t&acirc;m TP Cẩm Phả, ngay cạnh Vincom Plaza<br />\r\n	Đơn vị ph&acirc;n phối dự &aacute;n: Tập đo&agrave;n Đất Xanh<br />\r\n	Tổng diện t&iacute;ch&nbsp; đất quy hoạch tự x&acirc;y: 3,6ha<br />\r\n	Loại h&igrave;nh sản phẩm: đất nền tự x&acirc;y<br />\r\n	Tổng số l&ocirc; liền kề: 165 l&ocirc;<br />\r\n	Diện t&iacute;ch ph&acirc;n l&ocirc;: 76 m2 &ndash; 108m2<img alt=\"\" src=\"/public/upload/files/Green%20Park%20C%E1%BA%A9m%20Ph%E1%BA%A3/4c086e0949edacb3f5fc.jpg\" style=\"width: 1600px; height: 1198px;\" /></p>','<p>\r\n	- Dự &Aacute;n tọa lạc tại vị tr&iacute; chiến lược giữa trung t&acirc;m ph&aacute;t triển mới của th&agrave;nh phố Cẩm Phả, Ngay s&aacute;t Vincom Cẩm Phả, với c&aacute;c khu đ&ocirc; thị cao cấp xung quang như Vinhomes Cẩm Phả, Golden Mark Cẩm Phả&hellip;&nbsp; nơi c&oacute; nhiều tuyến đường giao th&ocirc;ng huyết mạch như Quốc lộ 18A nối Hạ Long &ndash; Cẩm Phả &ndash; M&oacute;ng C&aacute;i v&agrave; bến xe Cẩm Phả. Khu vực n&agrave;y cũng đang được quy hoạch trở th&agrave;nh v&ugrave;ng đất ph&aacute;t triển mới của tỉnh Quảng Ninh với hạ tầng đồng bộ, nhiều khu đ&ocirc; thị mới hiện đại c&ugrave;ng c&aacute;c khu trung t&acirc;m dịch vụ, vui chơi giải tr&iacute; s&ocirc;i động.<br />\r\n	- Vị tr&iacute; v&agrave;ng chiến lược n&agrave;y gi&uacute;p Green Park Cẩm&nbsp; Phả dễ d&agrave;ng kết nối tới c&aacute;c vị tr&iacute; quan trọng trong th&agrave;nh phố Cẩm Phả như: Khu trung t&acirc;m h&agrave;nh ch&iacute;nh, bệnh viện, trường học, bến cảng, c&aacute;c địa danh du lịch&hellip; Đồng thời rất thuận tiện khi giao thương với c&aacute;c khu vực sầm uất l&acirc;n cận như: V&acirc;n Đồn, M&oacute;ng C&aacute;i, Hạ Long&hellip; Đ&acirc;y l&agrave; vị tr&iacute; V&agrave;ng để ph&aacute;t triển m&ocirc; h&igrave;nh nh&agrave; phố thương mại sầm uất, s&ocirc;i động.</p>\r\n<p>\r\n	+ Ph&iacute;a Bắc gi&aacute;p cạnh Vincom Plaza, QL 18<br />\r\n	+ Ph&iacute;a Nam gi&aacute;p trục đường 30m, gi&aacute;p biển<br />\r\n	+ Ph&iacute;a T&acirc;y gi&aacute;p trường học, h&agrave;nh ch&iacute;nh c&ocirc;ng<br />\r\n	+ Ph&iacute;a Đ&ocirc;ng gi&aacute;p đường KĐT Vũng Đục FLC<br />\r\n	<img alt=\"\" src=\"/public/upload/files/Green%20Park%20C%E1%BA%A9m%20Ph%E1%BA%A3/cff6d468cda728f971b6.jpg\" style=\"width: 960px; height: 594px;\" /></p>','<p>\r\n	Mỗi ng&agrave;y an cư tại KĐT Green Park Cẩm Phả, cư d&acirc;n sẽ được tận hưởng một m&ocirc;i trường sống l&yacute; tưởng với sự kết hợp h&agrave;i h&ograve;a giữa cảnh quan xanh m&aacute;t v&agrave; tiện &iacute;ch thể thao, vui chơi, giải tr&iacute;, đa dạng.<br />\r\n	B&ecirc;n cạnh đ&oacute; l&agrave; khu ẩm thực, caf&eacute; với nhiều gian h&agrave;ng độc đ&aacute;o, ri&ecirc;ng biệt, được thiết kế theo phong c&aacute;ch trẻ trung, hiện đại, chia th&agrave;nh nhiều loại h&igrave;nh ẩm thực: nh&agrave; h&agrave;ng sang trọng phục vụ c&aacute;c buổi tiệc, cơm văn ph&ograve;ng, đồ ăn nhanh &ndash; cafe, cửa h&agrave;ng đồ uống, b&aacute;nh ngọt, kem tươi&hellip; l&agrave; điểm dừng ch&acirc;n l&yacute; tưởng gi&uacute;p Qu&yacute; kh&aacute;ch bổ sung th&ecirc;m năng lượng sau khi đ&atilde; c&oacute; những trải nghiệm th&uacute; vị với hoạt động mua sắm, vui chơi.<img alt=\"\" src=\"/public/upload/files/Green%20Park%20C%E1%BA%A9m%20Ph%E1%BA%A3/tienichnoikhu.jpg\" style=\"width: 1000px; height: 569px;\" /></p>','<p>\r\n	- Mặt bằng c&aacute;c l&ocirc; đồng đều, trải d&agrave;i theo shophouse Vincom v&agrave; Golden Mark.&nbsp;</p>\r\n<p>\r\n	- Dự &aacute;n khu đ&ocirc; thị Green park l&agrave; dự &aacute;n đất nền cẩm phả cuối c&ugrave;ng được ph&ecirc; duyệt dự x&acirc;y dựng</p>\r\n<p>\r\n	- Số lượng liền kề xung quanh TTTM Vincom thấp, chủ yếu l&agrave; đất biệt thự. Gi&aacute; liền kề sẽ tăng mạnh trong thời gian sắp tới do nhu cầu liền kề lớn.</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/Green%20Park%20C%E1%BA%A9m%20Ph%E1%BA%A3/m%E1%BA%B7t%20b%E1%BA%B1ng%202.jpg\" style=\"width: 1280px; height: 1086px;\" /></p>',NULL,NULL,NULL,'Tổng quan về dự án đất nền Green park Cẩm Phả','Vị Trí Dự án Green Park Cẩm Phả','Tiện ích dự án Green Park Cẩm Phả','Tiềm năng tăng giá của dự án còn rất lớn.',NULL,NULL,NULL,NULL,'Đất nền Green Park Cẩm Phả - Bảng giá trực tiếp Chủ Đầu Tư','23-5-2019','24-5-2019','green-park-cam-pha-trai-tim-moi-cua-cam-pha','Đất nền Green Park Cẩm Phả. Diện tích ~80m2. Đất nền sát cạnh Vincom. Giá Đầu Tư. Sổ đỏ vĩnh viễn. Mặt đường rộng. Vị trí trung tâm. Hạ tầng đã xong.',1,'Đất nền vincom Cẩm Phả, Đất nền Green Park Cẩm Phả, Đất nền liền kề cạnh vincom cẩm phả.','0'),(80,24,20,NULL,'ShopHouse Uông Bí New City - Cạnh VinCom Plaza',NULL,NULL,NULL,NULL,NULL,907,'/public/upload/images/uong-bi-new-city/8c682a2888016d5f3410.jpg',NULL,NULL,0,'1','<p>\r\n	Vị tr&iacute;: P. Y&ecirc;n Thanh, TP. U&ocirc;ng B&iacute;, tỉnh Quảng Ninh<br />\r\n	Chủ đầu tư: C&ocirc;ng ty Cổ phần Đầu tư X&acirc;y dựng v&agrave; Thương mại T&acirc;n Th&agrave;nh<br />\r\n	Diện t&iacute;ch đất nghi&ecirc;n cứu: 275.600 (m2)<br />\r\n	Diện t&iacute;ch đất x&acirc;y dựng: 916.840 (m2)<br />\r\n	Mật độ x&acirc;y dựng: 33,26%<br />\r\n	Loại h&igrave;nh ph&aacute;t triển: khu đ&ocirc; thị kiểu mẫu<br />\r\n	Quy m&ocirc; dự &aacute;n: 92 căn Shophouse thuận tiện kinh doanh&nbsp;<br />\r\n	Ph&acirc;n khu chức năng: Trung t&acirc;m thương mại, Trường học cấp 1, cấp 2, C&ocirc;ng vi&ecirc;n c&acirc;y xanh.<br />\r\n	Tư vấn thiết kế: C&ocirc;ng ty Cổ phần tư vấn Licogi<br />\r\n	Quản l&yacute; dự &aacute;n &amp; tư vấn gi&aacute;m s&aacute;t: tập đo&agrave;n Đất Xanh</p>','<p>\r\n	U&ocirc;ng B&iacute; l&agrave; th&agrave;nh phố trẻ năng động của tỉnh Quảng Ninh, những năm qua th&agrave;nh phố n&agrave;y c&oacute; tốc độ ph&aacute;t triển mạnh mẽ tr&ecirc;n tất cả c&aacute;c lĩnh vực. Trong đ&oacute;, phải kể đến:</p>\r\n<p>\r\n	Tốc độ tăng trưởng kinh tế đạt 13%/năm 2017 cao nhất từ trước đến nay<br />\r\n	Quy hoạch giao th&ocirc;ng &ndash; hạ tầng cơ sở ng&agrave;y c&agrave;ng ho&agrave;n thiện v&agrave; đồng bộ<br />\r\n	Thế mạnh về du lịch t&acirc;m linh &ndash; sinh th&aacute;i với nổi bật l&agrave; khu di t&iacute;ch Y&ecirc;n Tử, ch&ugrave;a Ba V&agrave;ng, ch&ugrave;a Hang Son, Khu du lịch sinh th&aacute;i Hồ Y&ecirc;n Trung, Lựng Xanh&hellip;</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/uong-bi-new-city/tam-thanh-tuyen-pho.jpg\" style=\"width: 800px; height: 450px;\" /></p>\r\n<p>\r\n	Đặc biệt với trị tr&iacute; l&agrave; cửa ng&otilde; ph&iacute;a T&acirc;y của Quảng Ninh, U&ocirc;ng B&iacute; sở hữu kết nối v&ugrave;ng:</p>\r\n<p>\r\n	Thuận tiện đi c&aacute;c tỉnh Hải Ph&ograve;ng, H&agrave; Nam, Hưng Y&ecirc;n, Bắc Ninh, Nam Định qua đường 10 v&agrave; đường 5<br />\r\n	Hai đầu th&agrave;nh phố lại tiếp gi&aacute;p với hai nền kinh tế năng động l&agrave; Thị x&atilde; Đ&ocirc;ng Triều v&agrave; Thị x&atilde; Quảng Y&ecirc;n, đem lại cho U&ocirc;ng B&iacute; nhiều động lực trong ph&aacute;t triển kinh tế v&agrave; tiềm năng đầu tư bất động sản.<br />\r\n	Được c&aacute;c chuy&ecirc;n gia bất động sản đ&aacute;nh gi&aacute; l&agrave; thị trường bất động sản lớn thứ hai Quảng Ninh, chỉ sau Hạ Long.</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/uong-bi-new-city/tam-thanh-shop-house.png\" style=\"width: 800px; height: 450px;\" /></p>','<p>\r\n	Dự &aacute;n U&ocirc;ng B&iacute; New City tọa lạc tại khu đất v&agrave;ng trung t&acirc;m th&agrave;nh phố U&ocirc;ng B&iacute;, nằm tại khu vực ph&iacute;a nam ng&atilde; ba giao giữa Quốc lộ 18A v&agrave; đường Trần Nh&acirc;n T&ocirc;ng thuộc phường Y&ecirc;n Thanh.</p>\r\n<p>\r\n	Ph&iacute;a Đ&ocirc;ng gi&aacute;p C&ocirc;ng ty TNHH Đức Lợi, khu d&acirc;n cư v&agrave; đất n&ocirc;ng nghiệp<br />\r\n	Ph&iacute;a T&acirc;y gi&aacute;p khu đ&ocirc; thị C&ocirc;ng Th&agrave;nh v&agrave; khu d&acirc;n cư<br />\r\n	Ph&iacute;a Nam gi&aacute;p khu d&acirc;n cư hiện trạng v&agrave; đất n&ocirc;ng nghiệp<br />\r\n	Ph&iacute;a Bắc gi&aacute;p Quốc lộ 18A</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/uong-bi-new-city/vi-tri-tan-thanh-uong-bi-new-city.png\" style=\"width: 800px; height: 467px;\" /></p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/uong-bi-new-city/05871f4c89b76be932a6.jpg\" style=\"width: 900px; height: 1200px;\" /></p>\r\n<p>\r\n	Quy hoạch 1/500 dự &aacute;n U&ocirc;ng B&iacute; New City</p>\r\n<p>\r\n	&nbsp;</p>','<p>\r\n	Sở hữu vị tr&iacute; ho&agrave;n hảo được đ&aacute;nh gi&aacute; l&agrave; &ldquo;tr&aacute;i tim&rdquo; của th&agrave;nh phố U&ocirc;ng B&iacute;, dự &aacute;n U&ocirc;ng B&iacute; New City &ocirc;m trọn trung t&acirc;m thương mại Vincom+ v&agrave; nằm trong khu vực c&oacute; mật độ d&acirc;n cư đ&ocirc;ng đ&uacute;c, sầm uất với nhịp sống năng đ&ocirc;ng bậc nhất th&agrave;nh phố. Chỉ trong v&ograve;ng b&aacute;n k&iacute;nh 2km, dự &aacute;n c&oacute; thể kết nối thuận tiện đến c&aacute;c tiện &iacute;ch nổi bật của TP U&ocirc;ng B&iacute; như:</p>\r\n<p>\r\n	Trường học: Trường học quốc tế li&ecirc;n cấp 1,2 Stephen Hawking một trong những trường quốc tế đầu ti&ecirc;n tại U&ocirc;ng B&Iacute; , Trường cao đẳng nghề Than &ndash; Kho&aacute;ng sản Quảng Ninh<br />\r\n	Bệnh viện: Bệnh viện đa khoa Việt Nam &ndash; Thụy Điển<br />\r\n	Trung t&acirc;m h&agrave;nh ch&iacute;nh &ndash; Văn h&oacute;a &ndash; Kinh tế: UBND phường Y&ecirc;n Thanh, CAND phường Y&ecirc;n Thanh, Ng&atilde; ba đường 10, Bến xe kh&aacute;ch, Ng&acirc;n h&agrave;ng, vườn hoa&hellip;<br />\r\n	Trung t&acirc;m thương mại: Liền kề Vincom+, Chợ đầu mối Y&ecirc;n Thanh, Chợ Thanh Sơn&hellip;<img alt=\"\" src=\"/public/upload/images/uong-bi-new-city/vi-tri-ket-noi-tan-thanh-uong-bi.jpg\" style=\"width: 800px; height: 533px;\" /></p>\r\n<p>\r\n	Quy hoạch v&agrave; ph&aacute;t triển đồng bộ tr&ecirc;n tổng diện t&iacute;ch khoảng 27,56 ha thuộc Phường Y&ecirc;n Thanh, Th&agrave;nh Phố U&ocirc;ng B&iacute;, tỉnh Quảng Ninh, dự &aacute;n U&ocirc;ng B&iacute; New City được x&acirc;y dựng tr&ecirc;n &yacute; tưởng một đ&ocirc; thị kiểu mẫu hiện đại, cộng đồng d&acirc;n cư cao cấp, m&ocirc;i trường sống văn minh, l&yacute; tưởng khi quy tụ h&agrave;ng loạt c&aacute;c tiện &iacute;ch dịch vụ chất lượng như:</p>\r\n<p>\r\n	C&ocirc;ng vi&ecirc;n c&acirc;y xanh<br />\r\n	Khu sinh hoạt cộng đồng<br />\r\n	B&atilde;i đỗ xe<br />\r\n	Trung t&acirc;m thương mại si&ecirc;u thị<br />\r\n	Kh&aacute;ch sạn, văn ph&ograve;ng<br />\r\n	Trường THCS, trường mẫu gi&aacute;o, nh&agrave; văn h&oacute;a<br />\r\n	Đường trải nhựa rộng, tho&aacute;ng.<br />\r\n	Với hơn 60% diện t&iacute;ch d&agrave;nh cho c&ocirc;ng vi&ecirc;n c&acirc;y xanh, cảnh quan v&agrave; c&aacute;c tiện &iacute;ch c&ocirc;ng cộng, Dự &aacute;n U&ocirc;ng B&iacute; New City xứng đ&aacute;ng l&agrave; lựa chọn l&yacute; tưởng cho một chốn an cư b&igrave;nh y&ecirc;n với kh&ocirc;ng gian sống xanh, tiện nghi ho&agrave;n hảo m&agrave; ai cũng muốn trở về sau những x&ocirc; bồ, ồn &atilde; của cuộc sống.</p>\r\n<p>\r\n	Dự &aacute;n xứng đ&aacute;ng l&agrave; Khu đ&ocirc; thị đ&aacute;ng sống bậc nhất tại th&agrave;nh phố U&ocirc;ng B&iacute;.</p>','<p>\r\n	<img alt=\"\" src=\"/public/upload/images/uong-bi-new-city/m%E1%BA%B7t%20b%E1%BA%B1ng%20n%C3%A9n.jpg\" style=\"width: 855px; height: 1200px;\" /></p>\r\n<p>\r\n	Mặt bằng ph&acirc;n khu Shophouse&nbsp;</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/uong-bi-new-city/9b0ee8cfd8e53dbb64f4.jpg\" style=\"width: 1280px; height: 720px;\" /></p>\r\n<p>\r\n	L&agrave; dự &aacute;n c&oacute; tổng mức đầu tư x&acirc;y dựng hạ tầng đ&ocirc; thị lớn nhất của TP U&ocirc;ng B&iacute; t&iacute;nh đến thời điểm hiện tại, U&ocirc;ng B&iacute; New City được quy hoạch ph&aacute;t triển một c&aacute;ch b&agrave;i bản, tiện &iacute;ch kh&eacute;p k&iacute;n, hạ tầng đồng bộ với c&aacute;c ph&acirc;n khu chức năng gồm khu Liền kề, biệt thự, shophouse, khu cao tầng, văn ph&ograve;ng, trung t&acirc;m thương mại, b&atilde;i đỗ xe, trường học, nh&agrave; văn h&oacute;a, khu sinh hoạt cộng đồng v&agrave; c&ocirc;ng vi&ecirc;n c&acirc;y xanh&hellip;</p>\r\n<p>\r\n	Đặc biệt, đường nội khu tại dự &aacute;n được trải đường nhựa, tho&aacute;ng rộng, vỉa h&egrave; l&aacute;t gạch được bố tr&iacute; cảnh quan c&acirc;y xanh, mạng lưới đ&egrave;n đường, hệ thống cấp tho&aacute;t nước&hellip;đều đ&atilde; ho&agrave;n thiện, khang trang, ph&aacute;p l&yacute; đẩy đủ, sổ đỏ l&acirc;u d&agrave;i. U&ocirc;ng B&iacute; New City xứng đ&aacute;ng l&agrave; khu đ&ocirc; thị c&oacute; kh&ocirc;ng gian sống văn minh, hiện đại, đ&aacute;p ứng mọi nhu cầu thiết thực nhất của cuộc sống.</p>\r\n<p>\r\n	&nbsp;</p>','<p>\r\n	C&aacute;c căn ShopHouse U&ocirc;ng B&iacute; New City đều được theo ph&ograve;ng c&aacute;ch thiết kế kiến tr&uacute;c Paris cổ điển sẽ mang lại cho kh&aacute;ch h&agrave;ng những gi&aacute; trị kh&ocirc;ng thể khước từ.&nbsp;<img alt=\"\" src=\"/public/upload/images/uong-bi-new-city/8c682a2888016d5f3410.jpg\" style=\"width: 800px; height: 500px;\" /></p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/uong-bi-new-city/44224363e14a04145d5b.jpg\" style=\"width: 1080px; height: 607px;\" /></p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/uong-bi-new-city/e85c5521910874562d19.jpg\" style=\"width: 1080px; height: 527px;\" /></p>\r\n<p>\r\n	&nbsp;</p>','<p>\r\n	<img alt=\"\" src=\"/public/upload/images/uong-bi-new-city/6f8a5798a27a47241e6b.jpg\" style=\"width: 900px; height: 1200px;\" /><img alt=\"\" src=\"/public/upload/images/uong-bi-new-city/2b136b0d55b9b7e7eea8.jpg\" style=\"width: 960px; height: 720px;\" /><img alt=\"\" src=\"/public/upload/files/6f8a5798a27a47241e6b.jpg\" style=\"width: 900px; height: 1200px;\" /></p>\r\n<p>\r\n	Shophouse đang trong qu&aacute; tr&igrave;nh ho&agrave;n thiện mặt ngo&agrave;i v&agrave; đi v&agrave;o b&agrave;n giao những căn đầu ti&ecirc;n cho kh&aacute;ch h&agrave;ng 7/2019</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/uong-bi-new-city/0eb92857ddb538eb61a4.jpg\" style=\"width: 1080px; height: 810px;\" /></p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/images/uong-bi-new-city/5d9b21d3d431316f6820.jpg\" style=\"width: 1080px; height: 810px;\" /><img alt=\"\" src=\"/public/upload/images/uong-bi-new-city/c02c9b5d6ebf8be1d2ae.jpg\" style=\"width: 1080px; height: 810px;\" /></p>\r\n<p>\r\n	Trường học QUốc tế li&ecirc;n cấp 1, 2 Stephen Hawking đang đi v&agrave;o ho&agrave;n thiện những c&ocirc;ng đoạn cuối c&ugrave;ng trước khi đ&oacute;n học sinh khai giảng th&aacute;ng 9/2019</p>',NULL,'Tổng quan thị trường Uông Bí','Vị trí dự án Shophouse Uông Bí New City',NULL,'mặt bằng dự án Uông Bí New City','Thiết kế Shophouse Uông Bí New City','Tiến độ hoàn thiện dự án Uông Bí New City',NULL,NULL,'Shophouse Uông Bí New City cơ hội đầu tư không thể khước từ.','24-5-2019','24-5-2019','shophouse-uong-bi-new-city-canh-vincom-plaza','Shophouse Uông Bí New city nhìn thẳng vào VIncom Plaza Uông Bí, cơ hội dầu tư và kinh doanh không thể khước từ nhà đầu tư.',27,'Shophouse Uông Bí, Nhà phố thương mại Uông Bí','0'),(84,24,23,NULL,'Chung cư LuxCity Cẩm Phả','Phường Cẩm Bình, Thành phố Cẩm Phả, Quảng Ninh',NULL,NULL,NULL,NULL,76,'/public/upload/files/LuxCity%20C%E1%BA%A9m%20Ph%E1%BA%A3/lux-city-co-chu-1.gif',NULL,NULL,1,'1','<p>\r\n	T&ecirc;n dự &aacute;n: FLC HALONG BAY GOLF CLUB &amp; LUXURY RESORT</p>\r\n<p>\r\n	&bull; Vị tr&iacute;: Phường Cẩm B&igrave;nh, Th&agrave;nh phố Cẩm Phả, Quảng Ninh<br />\r\n	&bull; Diện t&iacute;ch khu đất: 11.130 m2<br />\r\n	&bull; Diện t&iacute;ch đất x&acirc;y dựng: Nh&agrave; A1: 1493.3 m2<br />\r\n	&bull; Nh&agrave; A2: 863,5 m2<br />\r\n	&bull; Tổng diện t&iacute;ch s&agrave;n: 30911,3 m2<br />\r\n	&bull; Số tầng cao c&ocirc;ng tr&igrave;nh:<br />\r\n	Nh&agrave; A1: 11 tầng nổi, 1 tầng hầm<br />\r\n	Nh&agrave; A2: 15 tầng nổi, 2 tầng hầm<br />\r\n	&bull; Tổng số căn hộ: 334 căn hộ<br />\r\n	&bull; Mật độ x&acirc;y dựng: 35%<br />\r\n	&bull; Ng&agrave;y khởi c&ocirc;ng: Th&aacute;ng 9/2021<br />\r\n	&bull; Dự kiến ho&agrave;n th&agrave;nh: Qu&yacute; 1/2023</p>','<p>\r\n	<img alt=\"\" src=\"/public/upload/files/LuxCity%20C%E1%BA%A9m%20Ph%E1%BA%A3/33.png\" style=\"width: 1020px; height: 1141px;\" /></p>','<p>\r\n	Chung cư Luxcity Cẩm Phả nằm tr&ecirc;n địa b&agrave;n phường Cẩm B&igrave;nh, th&agrave;nh phố Cẩm Phả, tỉnh Quảng Ninh. Nằm tại khu vực được đ&aacute;nh gi&aacute; l&agrave; ph&aacute;t triển bậc nhất của th&agrave;nh phố Cẩm Phả, dự &aacute;n kh&ocirc;ng chỉ nằm cạnh QL 18A &ndash; huyết mạch kinh tế quan trọng của tỉnh, m&agrave; c&ograve;n gần c&aacute;c tiện &iacute;ch trọng yếu của th&agrave;nh phố v&agrave; tổ hợp giải tr&iacute; nằm dọc tuyến đường du lịch bạc tỉ &ndash; trục đường bao biển Hạ Long &ndash; Cẩm Phả.</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/LuxCity%20C%E1%BA%A9m%20Ph%E1%BA%A3/75f184d56f4f9911c05e-1024x655.jpg\" style=\"width: 1024px; height: 655px;\" /></p>\r\n<p>\r\n	<strong>VẠN TINH HOA QUY TỤ THỀM VỊNH XANH</strong><br />\r\n	Hoa lệ, kiều diễm b&ecirc;n vịnh di sản thế giới quyến rũ bạn bởi tuyệt t&aacute;c nghệ thuật đầy m&agrave;u sắc b&ecirc;n cạnh những d&ograve;ng chảy li&ecirc;n tục lu&acirc;n chuyển của nơi phồn vinh hội tụ. Luxcity Cẩm Phả bừng s&aacute;ng l&ecirc;n như một l&acirc;u đ&agrave;i tr&aacute;ng lệ toạ lạc giữa l&ograve;ng Th&agrave;nh phố Kho&aacute;ng Sản.</p>\r\n<p>\r\n	Phong c&aacute;ch thời thượng xa hoa v&agrave; tr&aacute;ng lệ bậc nhất thế giới của th&agrave;nh phố Las Vegas sẽ được thu gọn trong l&ograve;ng Luxcity Cẩm Phả c&ugrave;ng sự kết hợp ho&agrave;n hảo giữa nhịp sống năng động nơi th&agrave;nh phố kho&aacute;ng sản phồn hoa c&ugrave;ng những tiện &iacute;ch đẳng cấp hiện đại bậc nhất v&agrave; c&aacute;c tiện &iacute;ch trọng điểm của th&agrave;nh phố bao quanh dự &aacute;n như:</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/LuxCity%20C%E1%BA%A9m%20Ph%E1%BA%A3/Picture1-1024x874.jpg\" style=\"width: 1024px; height: 874px;\" /></p>','<p>\r\n	SỐNG B&Ecirc;N DI SẢN &ndash; S&Agrave;I NG&Agrave;N TIỆN &Iacute;CH<br />\r\n	Chung cư Luxcity Cẩm Phả đẹp v&agrave; đẳng cấp kh&ocirc;ng chỉ ở khung cảnh, ở những c&ocirc;ng tr&igrave;nh kiến tr&uacute;c m&agrave; c&ograve;n ở kh&ocirc;ng gian sống tiện nghi, ở sự cảm nhận khi bạn đặt ch&acirc;n đến mảnh đất n&agrave;y. Luxcity Cẩm Phả thu trọn Las Vegas v&agrave;o l&ograve;ng m&igrave;nh với 50++ tiện &iacute;ch nội, ngoại khu v&agrave; hệ sinh th&aacute;i vui chơi &ndash; giải tr&iacute; ho&agrave;n chỉnh bao quanh, sống tại Luxcity Cẩm Phả thực sự l&agrave; lựa chọn &ldquo;Th&ocirc;ng minh cho cuộc sống phồn vinh bất tận&rdquo; b&ecirc;n bờ vịnh B&aacute;i Tử Long.</p>\r\n<p>\r\n	50++ TIỆN &Iacute;CH ĐẲNG CẤP<br />\r\n	1. Khu vui chơi trẻ em ngo&agrave;i trời<br />\r\n	2. Đ&agrave;i phun nước trung t&acirc;m<br />\r\n	3. Khu leo thể chất cho b&eacute;<br />\r\n	4. Khu tập thể dục ngo&agrave;i trời<br />\r\n	5. Khu hoạt động tự do<br />\r\n	6. Khu tập thể thao<br />\r\n	7. S&acirc;n cầu l&ocirc;ng<br />\r\n	8. Ghế đ&aacute;<br />\r\n	9. M&aacute;y tập thể dục ngo&agrave;i trời<br />\r\n	10. Ch&ograve;i nghỉ 11. Cầu thang xuống tầng hầm A1<br />\r\n	12. Cầu thang xuống tầng hầm A2<br />\r\n	13. Nội khu<br />\r\n	14. Đường dạo bộ<br />\r\n	15. Khu nướng BBQ<br />\r\n	16. Khu vực ghế ngồi BBQ<br />\r\n	17. Bồn vệ sinh BBQ<br />\r\n	18. Ph&ograve;ng cộng đồng<br />\r\n	19. Ph&ograve;ng đa chức năng<br />\r\n	20. Sảnh lễ t&acirc;n t&ograve;a A1 31. Cổng an ninh<br />\r\n	32. Cổng ch&iacute;nh<br />\r\n	33. Cửa h&agrave;ng mua sắm<br />\r\n	34. Cửa h&agrave;ng TP tươi sống<br />\r\n	35. Nh&agrave; h&agrave;ng<br />\r\n	36. Qu&aacute;n cafe<br />\r\n	37. Si&ecirc;u thị mini<br />\r\n	38. Hiệu thuốc<br />\r\n	39. Ng&acirc;n hang<br />\r\n	40. Hầm để xe t&ograve;a A1<br />\r\n	41. Hầm để xe t&ograve;a A2 42. Ph&ograve;ng bảo tr&igrave;<br />\r\n	43. S&acirc;n chơi trẻ em trong nh&agrave;<br />\r\n	44. Khu wifi miễn ph&iacute;<br />\r\n	45. Vườn yoga<br />\r\n	46. Spa<br />\r\n	47. Trường mẫu gi&aacute;o<br />\r\n	48. Ph&ograve;ng Gym<br />\r\n	49. Ph&ograve;ng Yoga<br />\r\n	50. Đường chạy bộ<br />\r\n	51. Khu cắm trại ngo&agrave;i trời (tầng thượng)<br />\r\n	52. Vườn xanh (tầng thượng)</p>','<p>\r\n	<img alt=\"\" src=\"/public/upload/files/LuxCity%20C%E1%BA%A9m%20Ph%E1%BA%A3/mat-bang-can-ho.jpg\" style=\"width: 1024px; height: 724px;\" /></p>',NULL,NULL,NULL,'Tổng Quan','Vị trí','Tiện Ích','Mặt bằng dự án',NULL,NULL,NULL,NULL,'CHUNG CƯ CAO CẤP LUXCITY CẨM PHẢ – LASVEGAS GIỮA VỊNH KỲ QUAN','23-10-2021','23-10-2021','chung-cu-luxcity-cam-pha','Chung cư Luxcity Cẩm Phả là chung cư cao cấp đầu tiên xuất hiện tại thành phố khoáng sản với 50+ tiện ích hiện đại bậc nhất hiện nay hứa hẹn mang đến',0,'LuxCity Cẩm Phả','0'),(85,24,22,NULL,'NHÀ PHỐ LIỀN KỀ GREEN PARK HÀ PHONG HẠ LONG','Hà Phong, Hạ Long',NULL,NULL,NULL,NULL,112,'/public/upload/files/h%C3%A0%20phong/z2596270569698_1cfa114b169bd4c36922d3b8143a515a.jpg',NULL,NULL,0,'1','<p>\r\n	Vị tr&iacute;: Phường H&agrave; Phong, Th&agrave;nh Phố Hạ Long, Tỉnh Quảng Ninh</p>\r\n<p>\r\n	Chủ đầu tư: C&ocirc;ng ty Cổ Phần Minh Anh</p>\r\n<p>\r\n	Quy m&ocirc;: 15.973,98 m2</p>\r\n<p>\r\n	Loại h&igrave;nh ph&aacute;t triển dự &aacute;n: nh&agrave; phố liền kề</p>\r\n<p>\r\n	Đường nội bộ: 7,5 &ndash; 10,5m</p>\r\n<p>\r\n	Số tầng x&acirc;y dựng: 5 tầng</p>\r\n<p>\r\n	Diện t&iacute;ch mỗi l&ocirc;: 96.25 &ndash; 126.8m2</p>\r\n<p>\r\n	Ph&aacute;p l&yacute; minh bạch</p>','<p>\r\n	Tổng quan dự &aacute;n<br />\r\n	T&ecirc;n dự &aacute;n: Nh&agrave; phố liền kề Green Park H&agrave; Phong</p>\r\n<p>\r\n	Vị tr&iacute;: Phường H&agrave; Phong, Th&agrave;nh Phố Hạ Long, Tỉnh Quảng Ninh</p>\r\n<p>\r\n	Chủ đầu tư: C&ocirc;ng ty Cổ Phần Minh Anh</p>\r\n<p>\r\n	Quy m&ocirc;: 15.973,98 m2</p>\r\n<p>\r\n	Loại h&igrave;nh ph&aacute;t triển dự &aacute;n: nh&agrave; phố liền kề</p>\r\n<p>\r\n	Đường nội bộ: 7,5 &ndash; 10,5m</p>\r\n<p>\r\n	Số tầng x&acirc;y dựng: 5 tầng</p>\r\n<p>\r\n	Diện t&iacute;ch mỗi l&ocirc;: 96.25 &ndash; 126.8m2</p>\r\n<p>\r\n	Ph&aacute;p l&yacute; minh bạch</p>','<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Ph&iacute;a Đ&ocirc;ng Bắc gi&aacute;p suối Lộ phong<br />\r\n	Ph&iacute;a T&acirc;y Bắc gi&aacute;p C&ocirc;ng ty c&ocirc; phần đăng kiểm xe cơ giới đường bộ Quảng Ninh v&agrave; quốc lộ 18A<br />\r\n	Ph&iacute;a Đ&ocirc;ng Nam v&agrave; Ph&iacute;a T&acirc;y Nam gi&aacute;p khu d&acirc;n cư hiện trạng</p>\r\n<h2>\r\n	<strong>Tiềm năng đa kết nối của Green Park H&agrave; Phong</strong></h2>\r\n<p>\r\n	<strong><img alt=\"\" src=\"/public/upload/files/h%C3%A0%20phong/z2603585372137_aea4ac2a49c3659154a17f1293d56df5-1024x739.jpg\" style=\"width: 1024px; height: 739px;\" /></strong></p>\r\n<p>\r\n	<strong>Toạ lạc tại Phường H&agrave; Phong, Th&agrave;nh Phố Hạ Long, Tỉnh Quảng Ninh, nh&agrave; phố thương mại Green Park H&agrave; Phong được đ&aacute;nh gi&aacute; l&agrave; &ldquo;Huyết mạch&rdquo; của tỉnh, dự &aacute;n nh&agrave; phố liền kề Green Park H&agrave; Phong may mắn sở hữu những quỹ đất cuối c&ugrave;ng của TP Hạ Long. Nằm trong khu d&acirc;n cư l&acirc;u năm của Th&agrave;nh Phố Hạ Long, chỉ trong b&aacute;n k&iacute;nh ngắn, dự &aacute;n c&oacute; thể kết nối thuận tiện đến c&aacute;c tiện &iacute;ch nổi bật như:</strong></p>\r\n<p>\r\n	<strong>&ndash; S&aacute;t QL 18 A</strong></p>\r\n<p>\r\n	<strong>&ndash; Trung t&acirc;m h&agrave;nh ch&iacute;nh &ndash; Văn h&oacute;a &ndash; Kinh tế như:</strong></p>\r\n<p>\r\n	<strong>+ S&acirc;n b&oacute;ng</strong></p>\r\n<p>\r\n	<strong>+ Chợ H&agrave; Phong</strong></p>\r\n<p>\r\n	<strong>+ UBND Phường H&agrave; Phong,</strong></p>\r\n<p>\r\n	<strong>+ UBND tỉnh Quảng Ninh,</strong></p>\r\n<p>\r\n	<strong>+ H&agrave;nh ch&iacute;nh c&ocirc;ng tỉnh Quảng Ninh</strong></p>\r\n<p>\r\n	<strong>+ Bến xe kh&aacute;ch</strong></p>\r\n<p>\r\n	<strong>&ndash; Trường học c&aacute;c cấp:</strong></p>\r\n<p>\r\n	<strong>+ Trường THCS Minh Khai</strong></p>\r\n<p>\r\n	<strong>+ Trường THPT Vũ Văn Hiếu</strong></p>\r\n<p>\r\n	<strong>+ Trường tiểu học Minh H&agrave;</strong></p>\r\n<p>\r\n	<strong>&ndash; C&aacute;c trung t&acirc;m thương mại sầm uất:</strong></p>\r\n<p>\r\n	<strong>+ Megamarket</strong></p>\r\n<p>\r\n	<strong>+ Go</strong></p>\r\n<p>\r\n	<strong>&ndash; C&aacute;c di t&iacute;ch lịch sử văn h&oacute;a như: Đ&igrave;nh l&agrave;ng Lộ Phong, ch&ugrave;a Quang Nghi&ecirc;m, huyền thoại Rốn C&ocirc; Ti&ecirc;n &hellip;</strong></p>\r\n<p>\r\n	&nbsp;</p>','<p>\r\n	Tiện &iacute;ch đẳng cấp, hạ tầng đồng bộ, kiến tr&uacute;c &Aacute; &Acirc;u hiện đại sang trọng<br />\r\n	Green Park H&agrave; Phong Hạ Long được thiết kế lấy cảm hứng từ truyền thuyết Rốn C&ocirc; Ti&ecirc;n của địa phương, nhằm mang lại t&agrave;i lộc, thịnh vượng v&agrave; một cuộc sống an cư th&agrave;nh đạt cho chủ nh&acirc;n tương lai.</p>\r\n<p>\r\n	Tự tin l&agrave; một trong những dự &aacute;n ti&ecirc;n phong tại khu vực t&iacute;nh đến thời điểm hiện tại, với t&acirc;m điểm của dự &aacute;n l&agrave; lối kiến tr&uacute;c cổ điển sang trọng mang phong c&aacute;ch &Aacute; &Acirc;u, tỉ mỉ đến từng chi tiết cho căn nh&agrave;, tạo n&ecirc;n một phong c&aacute;ch sống đẳng cấp, độc đ&aacute;o v&agrave; kh&aacute;c biệt c&ugrave;ng sự kết hợp với c&aacute;c tiện &iacute;ch đẳng cấp như:</p>\r\n<p>\r\n	C&ocirc;ng vi&ecirc;n c&acirc;y xanh</p>\r\n<p>\r\n	Khu&ocirc;n vi&ecirc;n dạo bộ</p>\r\n<p>\r\n	B&atilde;i đỗ xe&hellip;&hellip;</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/h%C3%A0%20phong/z2596270565105_e4c0f74d23117f9127e4c8c29b3963ed-1024x576.jpg\" style=\"width: 1024px; height: 576px;\" /></p>\r\n<p>\r\n	Với quy m&ocirc; dự &aacute;n l&ecirc;n tới 15.973.98 m2, hệ thống nh&agrave; phố liền kề tại Green Park H&agrave; Phong c&oacute; diện t&iacute;ch đa dạng từ 96 &ndash; 129m2, mặt tiền tho&aacute;ng rộng 6-12m, được thiết kế đồng bộ về m&agrave;u sắc v&agrave; kiến tr&uacute;c tạo n&ecirc;n cảnh quan tuyến phố sinh động, đẹp mắt. Đặc biệt 52 căn nh&agrave; liền kề tại Green Park H&agrave; Phong được chủ đầu tư ho&agrave;n thiện mặt ngo&agrave;i, như vậy chủ nh&acirc;n c&oacute; thể&nbsp; tự do s&aacute;ng tạo thiết kế nội thất b&ecirc;n trong ph&ugrave; hợp với nhu cầu sử dụng của m&igrave;nh.</p>\r\n<p>\r\n	Nh&agrave; phố liền kề Green Park H&agrave; Phong &ndash;&nbsp; &ldquo;Sống thời thượng &ndash; hưởng phồn vinh&rdquo;<br />\r\n	Với mong muốn kiến tạo một khu đ&ocirc; thị mới hiện đại &ndash; đồng bộ sang trọng với vẻ đẹp cổ điển, quyến rũ. Những căn nh&agrave; phố liền kề ở Green Park H&agrave; Phong hiện l&ecirc;n như một th&agrave;nh đ&ocirc; tr&aacute;ng lệ m&agrave; bất cứ ai một lần nh&igrave;n thấy đều kh&ocirc;ng khỏi ngỡ ng&agrave;ng, m&ecirc; đắm.</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/h%C3%A0%20phong/z2596270544567_d24700c38ed3b4f096ad324d57db57ed-1024x576.jpg\" /></p>\r\n<p>\r\n	C&ugrave;ng những ưu thế vượt trội về vị tr&iacute;, diện t&iacute;ch, thiết kế, dự &aacute;n được đ&aacute;nh gi&aacute; l&agrave; một trong những d&ograve;ng sản phẩm HOT nhất thị trường BĐS hạ long hiện nay c&ugrave;ng t&iacute;nh thanh khoản cao, hứa hẹn mang đến cơ hội đầu tư sinh lời v&ocirc; c&ugrave;ng lớn cho c&aacute;c chủ nh&acirc;n tương lai.</p>','<p>\r\n	<img alt=\"\" src=\"/public/upload/files/h%C3%A0%20phong/matbangsodo_haphonga3_0207-out-02-1024x724.png\" style=\"width: 1024px; height: 724px;\" /></p>\r\n<h1>\r\n	Mặt bằng căn điển h&igrave;nh</h1>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/h%C3%A0%20phong/ttv_haphong_2605_outm2-scaled.jpg\" style=\"width: 1024px; height: 648px;\" /></p>',NULL,NULL,NULL,'Tổng Quan','Vị trí “trái tim” Hà Phong – Quảng Ninh','Tiện Ích','Mặt Bằng',NULL,NULL,NULL,NULL,'Dự án Nhà phố liền kề Green Park Hà Phong sở hữu nút giao vàng giữa 2 thành phố lớn là Hạ Long và Cẩm Phả tại Quảng Ninh','25-10-2021',NULL,'nha-pho-lien-ke-green-park-ha-phong-ha-long','Nằm cạnh Quốc lộ 18A từ Hạ Long đi tới Cẩm Phả và nằm trên trục chính xuống đường Bao Biển cách chỉ 1km cùng với lợi thế phát triển kinh tế – giao thư',0,'Hà Phong Green Prak','0'),(86,24,23,NULL,'Green Dragon City Cẩm Phả','Cẩm Trung – Cẩm Thành – Cẩm Bình, TP. Cẩm Phả',NULL,NULL,NULL,NULL,739,'/public/upload/files/ttp/dadd0c7dc7b033ee6aa163.jpg','40tr/m2',NULL,0,'1','<p>\r\n	Tọa lạc tại vị tr&iacute; đắc địa, nằm trong v&ugrave;ng l&otilde;i quy hoạch của th&agrave;nh phố Cẩm Phả, Green Dragon City sở hữu nhiều lợi thế v&agrave;ng về giao th&ocirc;ng hạ tầng cũng như kinh tế x&atilde; hội.</p>\r\n<p>\r\n	Green Dragon City sở hữu vị tr&iacute; t&acirc;m điểm s&ocirc;i động của th&agrave;nh phố Cẩm Phả<br />\r\n	Thế đất địa linh vương kh&iacute;, &ldquo;Tựa sơn &ndash; Nghinh hải&rdquo; =&gt; rất tốt về mặt phong thủy, gi&uacute;p mang lại sự tr&ugrave; ph&uacute; gi&agrave;u c&oacute; cho gia chủ.<br />\r\n	Green Dragon City l&agrave; dự &aacute;n hiếm hoi tại khu vực sở hữu đường bờ biển d&agrave;i hơn 3km ven vịnh B&aacute;i Tử Long với g&oacute;c nh&igrave;n to&agrave;n cảnh Vinh xanh v&agrave; hướng ra bến du thuyền đẳng cấp chỉ v&agrave;i bước ch&acirc;n cư d&acirc;n c&oacute; thể chạm tới l&agrave;n nước xanh m&aacute;t.<br />\r\n	Dự &aacute;n tiếp gi&aacute;p c&aacute;c trục giao th&ocirc;ng huyết mạch được coi l&agrave; 4 động lực kinh tế lớn của tỉnh Quảng Ninh: Tuyến đường bao biển Hạ Long &ndash; V&acirc;n Đồn, Cảng h&agrave;ng kh&ocirc;ng Quốc tế V&acirc;n Đồn, cao tốc H&agrave; Nội &ndash; Hạ Long &ndash; V&acirc;n Đồn, Đường Cao tốc Nội B&agrave;i &ndash; Hạ Long &ndash; M&oacute;ng C&aacute;i&hellip;gi&uacute;p cho việc đi lại, kết nối c&aacute;c tiện &iacute;ch, giao thương với c&aacute;c khu vực l&acirc;n cận được thuận tiện.</p>','<p>\r\n	T&ecirc;n dự &aacute;n: Dự &aacute;n khu đ&ocirc; thị du lịch, dịch vụ Bái Tử Long 1<br />\r\n	T&ecirc;n thương mại: KĐT Green Dragon City<br />\r\n	Chủ đầu tư: C&ocirc;ng ty Cổ phần T&acirc;̣p đoàn Đầu tư Ph&aacute;t triển C&ocirc;ng nghiệp TTP<br />\r\n	Vị tr&iacute;: Thuộc địa phận 3 phường: Cẩm Trung &ndash; Cẩm Th&agrave;nh &ndash; Cẩm B&igrave;nh, TP. Cẩm Phả<br />\r\n	Ph&aacute;p l&yacute;: Dự &aacute;n đ&atilde; ho&agrave;n thiện đầy đủ hồ sơ ph&aacute;p l&yacute;, sổ đỏ từng l&ocirc;<br />\r\n	Tổng diện t&iacute;ch đất: 99,2 ha<br />\r\n	Loại h&igrave;nh: Biệt thự, liền kề, shophouse, tổ hợp kh&aacute;ch sạn thương mại dịch vụ<br />\r\n	Đơn vị thiết kế: Kume Sekkie &ndash; Đơn vị thiết kế h&agrave;ng đầu Nhật Bản<br />\r\n	Hệ thống tiện &iacute;ch: 20+ tiện &iacute;ch đẳng cấp chuẩn 5 sao.</p>','<p>\r\n	Tọa lạc tại vị tr&iacute; đắc địa, nằm trong v&ugrave;ng l&otilde;i quy hoạch của th&agrave;nh phố Cẩm Phả, Green Dragon City sở hữu nhiều lợi thế v&agrave;ng về giao th&ocirc;ng hạ tầng cũng như kinh tế x&atilde; hội.</p>\r\n<p>\r\n	Green Dragon City sở hữu vị tr&iacute; t&acirc;m điểm s&ocirc;i động của th&agrave;nh phố Cẩm Phả<br />\r\n	Thế đất địa linh vương kh&iacute;, &ldquo;Tựa sơn &ndash; Nghinh hải&rdquo; =&gt; rất tốt về mặt phong thủy, gi&uacute;p mang lại sự tr&ugrave; ph&uacute; gi&agrave;u c&oacute; cho gia chủ.<br />\r\n	Green Dragon City l&agrave; dự &aacute;n hiếm hoi tại khu vực sở hữu đường bờ biển d&agrave;i hơn 3km ven vịnh B&aacute;i Tử Long với g&oacute;c nh&igrave;n to&agrave;n cảnh Vinh xanh v&agrave; hướng ra bến du thuyền đẳng cấp chỉ v&agrave;i bước ch&acirc;n cư d&acirc;n c&oacute; thể chạm tới l&agrave;n nước xanh m&aacute;t.<br />\r\n	Dự &aacute;n tiếp gi&aacute;p c&aacute;c trục giao th&ocirc;ng huyết mạch được coi l&agrave; 4 động lực kinh tế lớn của tỉnh Quảng Ninh: Tuyến đường bao biển Hạ Long &ndash; V&acirc;n Đồn, Cảng h&agrave;ng kh&ocirc;ng Quốc tế V&acirc;n Đồn, cao tốc H&agrave; Nội &ndash; Hạ Long &ndash; V&acirc;n Đồn, Đường Cao tốc Nội B&agrave;i &ndash; Hạ Long &ndash; M&oacute;ng C&aacute;i&hellip;gi&uacute;p cho việc đi lại, kết nối c&aacute;c tiện &iacute;ch, giao thương với c&aacute;c khu vực l&acirc;n cận được thuận tiện.</p>\r\n<p>\r\n	<img alt=\"\" src=\"/public/upload/files/ttp/Toroi_ttp_0812_1-e1608890994444-1024x658.jpg\" style=\"width: 1024px; height: 658px;\" /></p>',NULL,'<p>\r\n	<img alt=\"\" src=\"/public/upload/files/ttp/To%20gap%20mat%202%20ha%20tang%201%20crop.jpg\" /></p>',NULL,NULL,NULL,'Tổng Quan','Vị Trí',NULL,'Mặt Bằng',NULL,NULL,NULL,NULL,'GREEN DRAGON CITY – TÂM NGỌC CỦA THÀNH PHỐ CẨM PHẢ','9-11-2021','16-11-2021','green-dragon-city-cam-pha','Tọa lạc tại vị trí đắc địa, nằm trong vùng lõi quy hoạch của thành phố Cẩm Phả, Green Dragon City sở hữu nhiều lợi thế vàng về giao thông hạ tầng cũng',0,'TTP cẩm phả','0');
